public void lookup()
{
    BudgetReservation_PSN::showBudgetReservationLookup(purchReqLine, this.formRun(), this);
}

public void clicked()
{
    BudgetReservationLineRecId_PSN      budgetReservationLineRecId;
    BudgetReservationLineSelectorContract_PSN   budgetResLineSelectorContract;

    budgetResLineSelectorContract = BudgetReservationLineSelectorContract_PSN::newFromParameters(
        BudgetReservationRelievingDocument_PSN::PurchaseRequisition,
        purchReqLine.TransDate,
        purchReqLine.PurchAgreement);

    budgetResLineSelectorContract.parmDoShowEditableSelectorForm(PurchReqTableForm.canBudgetReservationLineBeEdited_PSN(purchReqLine_ds));

    BudgetReservationLineSelectorForm_PSN::showBudgetResLineSelector(
        budgetResLineSelectorContract,
        purchReqLine,
        element);

    budgetReservationLineRecId = budgetResLineSelectorContract.getSingleBudgetReservationLineRecId();

    if (budgetReservationLineRecId)
    {
        purchReqLine.BudgetReservationLine_PSN = budgetReservationLineRecId;

        if (purchReqLine.validateField(fieldNum(PurchReqLine, BudgetReservationLine_PSN)))
        {
            purchReqLine.modifiedField(fieldNum(PurchReqLine, BudgetReservationLine_PSN));

            purchReqLine_ds.refresh();
            element.setFieldAccess();
        }
        else
        {
            purchReqLine.BudgetReservationLine_PSN = purchReqLine.orig().BudgetReservationLine_PSN;
        }
    }
}

