}

public void textChange()
{
    super();

    if (this.text() == "")
    {
        this.reinitializeField();
    }
}

public boolean modified()
{
    boolean ret;

    if (this.text() == "" && !isLookupRun && PurchReqLineEditPurchAgreementFlight::instance().isEnabled())
    {
        this.reinitializeField();
    }

    ret = super();

    isLookupRun = false;

    return ret;
}

private void reinitializeField()
{
    purchReqLine.modifiedFieldVendAccount(false);

    purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, editPurchAgreementId));
}

public Common lookupReference()
{
    if (PurchReqTableDeliveryAddressLookupPurchFlight::instance().isEnabled()
        && purchReqLine.LineType == PurchReqLineType::Item)
    {
        LogisticsLocationSelectionLookup_Purch lookup = element.createDeliveryPostalAddressLookup(this, inventDim);

        return lookup.performFormLookup();
    }
    else
    {
        SysReferenceTableLookup sysTableLookup;
        Query                   query;

        query = purchReqLine.defaultAddressQuery();

        sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(LogisticsPostalAddress), this);
        sysTableLookup.addLookupfield(fieldNum(LogisticsPostalAddress, Location));
        sysTableLookup.addLookupfield(fieldNum(LogisticsPostalAddress, Street));
        sysTableLookup.addLookupfield(fieldNum(LogisticsPostalAddress, City));
        sysTableLookup.addLookupfield(fieldNum(LogisticsPostalAddress, State));
        sysTableLookup.addLookupfield(fieldNum(LogisticsPostalAddress, ZipCode));

        sysTableLookup.parmQuery(query);

        return sysTableLookup.performFormLookup();
    }

}

public Common resolveReference()
{
    Common ret;

    ret = super();

    return ret;
}

