public void pageActivated()
{
    super();

    element.setLineAssetValueAccess(hasQualifier, hasQualifier);
}

void clicked()
{
    container assetRules = purchReqLine.findAndProcessRule();

    if (conLen(assetRules) > 0 && !conLen(conPeek(assetRules, 5)) > 0)
    {
        info("@SYS326983");
    }

    if (conLen(assetRules) >= 5 && conLen(conPeek(assetRules, 5)) > 0)
    {
        info("@SYS136020");
    }

    purchReqLine.updateAssetGroup(true);
    element.setAssetGroupAccess();
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if (!isAssetGroupModified)
    {
        purchReqLine_ds.clearAssetQualifier();
    }

    isAssetGroupModified = false;

    return ret;
}

public void lookup()
{
    Object                           formRun;
    Args                             args;
    AssetRuleTmpAssetQualifierLookup assetRuleTmpAssetQualifierLookup;
    boolean                          isGlobalRule;
    container                        assetRules;

    if (purchReqLine.ProcurementCategory && purchReqLine.TransDate && purchReqLine.CurrencyCode)
    {
        assetRules      = purchReqLine.findAndProcessRule();
        isGlobalRule    = conPeek(assetRules, GlobalRuleUsedPos);
    }
    args = new Args();
    args.record(purchReqLine.buildAssetRuleTmpAssetQualifierLookup(assetRules));
    args.name(formStr(AssetRuleAssetQualifierLookup));
    args.caller(this);

    xFormRun xFormRun = classfactory.formRunClass(args);
    formRun = xFormRun as FormRun;
    xformRun.init();

    this.performFormLookup(xformRun);
    xformRun.wait();

    AssetRuleIAssetQualifierLookup assetRuleAssetQualifierLookup = FormRun as AssetRuleIAssetQualifierLookup;
    assetRuleTmpAssetQualifierLookup = assetRuleAssetQualifierLookup.getPurchReqQualifierLookup();

    if (assetRuleTmpAssetQualifierLookup)
    {
        purchReqLine.setAssetRuleQualifierOption(assetRuleTmpAssetQualifierLookup, isGlobalRule);

        purchReqLine.doUpdate();

        element.setLineAssetValueAccess(true,true);
    }
    purchReqLine_ds.reread();
}

/// <summary>
/// Adds the 'Go to Main Table Form' option to the context menu for reason code field.
/// </summary>
public void jumpRef()
{
    //Pass the account type of Fixed Assets, so that the relevant form shows.
    ReasonTable::jumpRef(ReasonCodeAccountTypeAll::FixedAssets);
}

/// <summary>
/// Constructs a lookup for the reason code field.
/// </summary>
/// <remarks>
/// The lookup shows the appropriate reason codes (if any) given the offset and main account selections.
/// </remarks>
public void lookup()
{
    //Calls the lookup method passing Fixed Assets as both main and offset account types; boolean values of true
    //and false are passed for them respectively, since the offset account is not present in this journal.
    ReasonFormTable::reasonCodeLookup(this, LedgerJournalACType::FixedAssets, LedgerJournalACType::FixedAssets, true, false);
}

