[Form]
public class PurchSelectLines extends FormRun
{
    PurchSelectLinesManager purchSelectLinesManager;
    TradeLineRefId tradeLineRefId;
    boolean selectionHasChanged;
    PurchFormLetter localPurchFormLetter;
    boolean cancelClicked;
    FormObservableLink lineIncludedFormObservable = new FormObservableLink();

}

/// <summary>
/// Queries the user if they want to discard any changes they made.
/// </summary>
/// <returns>
/// true if there has been no changes or if the user replies "Yes" to the confirmation; otherwise false.
/// </returns>
public boolean canClose()
{
    boolean ret = super();

    if (!this.closedOk() && !cancelClicked)
    {
        // User closed the form with the closebox, if anything changed, ask if they want to cancel
        if (selectionHasChanged)
        {
            if (Box::yesNo("@SYS128021", DialogButton::Yes) != DialogButton::Yes)
            {
                ret = false;
            }
        }
    }

    return ret;
}

void checkIfMiscChargesCreditDebitTypeItem()
{
    #resAppl
    ImageRes icon = purchTable.checkIfHeaderMiscChargesItems();

    if (icon == #ImageWarning )
    {
        checkFailed("@SYS115806");
    }
}

void enableButtons()
{
    buttonIncludeAll.enabled(!purchSelectLinesManager.areAllPOMarked());
    buttonExcludeAll.enabled(!purchSelectLinesManager.areAllPOUnmarked());
}

/// <summary>
/// Initializes local variables of the <c>PurchSelectLines</c> form.
/// </summary>
public void init()
{
    VendInvoiceInfoTable parmTable;

    super();

    selectionHasChanged = false;
    parmTable = element.args().record();

    tradeLineRefId = parmTable.TableRefId;
    localPurchFormLetter = element.args().parmObject();

    //Pass in the parmId session value for the PurchSelectLines to retrieve proper records.
    purchSelectLinesManager = PurchSelectLinesManager::newPurchSelectLinesManager(tradeLineRefId);
    purchSelectLinesManager.parmPurchTableMark(purchTableMark);
    purchSelectLinesManager.parmPurchLineMark(purchLineMark);
    purchSelectLinesManager.parmPurchTable_ds(purchTable_ds);
    purchSelectLinesManager.parmPurchLine_ds(purchLine_ds);
    purchSelectLinesManager.parmParmId(parmTable.ParmId);
    purchSelectLinesManager.init();

    element.refreshDataSources();
}

/// <summary>
/// Refreshes the purchTable_ds and purchLine_ds FormDataSources.
/// </summary>
public void refreshDataSources()
{
    purchTable_ds.refresh();
    purchLine_ds.refresh();
}

public void run()
{
    super();
    element.enableButtons();
    purchTable_InvoiceAccount.setFocus();
}

/// <summary>
/// Indicates whether the user has marked the PurchLine.
/// </summary>
/// <param name="_set">
/// true if the value is being set and retrieved, false if the value is only being retrieved.
/// </param>
/// <param name="_purchLine">
/// The purchLine buffer which is being marked or unmarked.
/// </param>
/// <param name="_included">
/// true if the PurchLine record is being marked; otherwise false
/// </param>
/// <returns>
/// Yes if the row is marked; otherwise No.
/// </returns>
public edit NoYes included(boolean _set,PurchLine _purchLine,NoYes _included)
{
    NoYes localNoYes;

    localNoYes = purchSelectLinesManager.mark('', _purchLine.RecId, purchLine, _included, NoYes::No, _set);

    if (_set)
    {
        element.refreshDataSources();
    }

    return localNoYes;
}

public int active()
{
    int ret = super();

    element.enableButtons();

    return ret;
}

public void init()
{
    QueryBuildRange rangeIsDeleted;

    super();

    rangeIsDeleted = this.query().dataSourceTable(tablenum(PurchLine)).addRange(fieldnum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
}

/// <summary>
/// Indicates whether the user has marked the PurchTable.
/// </summary>
/// <param name="_set">
/// true if the value is being set and retrieved, false if the value is only being retrieved.
/// </param>
/// <param name="_purchTable">
/// The purchTable buffer which is being marked or unmarked.
/// </param>
/// <param name="_included">
/// true if the PurchTable record is being marked; otherwise false
/// </param>
/// <returns>
/// Yes if the row is marked; otherwise No.
/// </returns>
public edit NoYes included(boolean _set,PurchTable _purchTable,NoYes _included)
{
    NoYes localNoYes = purchSelectLinesManager.mark(_purchTable.PurchId, 0, purchLine, _included, NoYes::Yes, _set);

    if (_set)
    {
        // Must call linkActive in order to force refreshing of edit method values.
        purchLine_ds.linkActive();
    }

    return localNoYes;
}

public int active()
{
    int ret = super();

    element.enableButtons();

    return ret;
}

/// <summary>
/// Indicates whether the user has marked the PurchTable.
/// </summary>
/// <param name="_set">
/// true if the value is being set and retrieved, false if the value is only being retrieved.
/// </param>
/// <param name="_purchTableMark">
/// The purchTableMarkTmp buffer which is being marked or unmarked.
/// </param>
/// <param name="_included">
/// true if the PurchTable record is being marked; otherwise false
/// </param>
/// <returns>
/// Yes if the row is marked; otherwise No.
/// </returns>
public edit NoYes included(boolean _set, PurchTableMarkTmp _purchTableMark, NoYes _included)
{
    PurchTable correspondingPurchTable;

    lineIncludedFormObservable.observe();

    correspondingPurchTable = PurchTable::find(_purchTableMark.PurchId);

    return purchTable_ds.included(_set, correspondingPurchTable, _included);
}

/// <summary>
/// Indicates whether the user has marked the PurchLine.
/// </summary>
/// <param name="_set">
/// true if the value is being set and retrieved, false if the value is only being retrieved.
/// </param>
/// <param name="_purchLineMark">
/// The PurchLineMarkTmp record representing the PurchLine buffer which is being marked or unmarked.
/// </param>
/// <param name="_included">
/// true if the PurchLine record is being marked; otherwise false
/// </param>
/// <returns>
/// Yes if the row is marked; otherwise No.
/// </returns>
public edit NoYes included(boolean _set, PurchLineMarkTmp _purchLineMark, NoYes _included)
{
    PurchLine correspondingPurchLine;

    correspondingPurchLine = PurchLine::findRecId(_purchLineMark.PurchLine);

    return purchLine_ds.included(_set, correspondingPurchLine, _included);
}

/// <summary>
/// Calls the markAll method of the <c>purchSelectLinesManager</c> class and
/// indicates that the user has changed the default values.
/// </summary>
void clicked()
{
    super();

    purchSelectLinesManager.markAll();

    lineIncludedFormObservable.markChanged();

    selectionHasChanged = true;

    element.refreshDataSources();
}

/// <summary>
/// Calls the unMarkAll method of the <c>purchSelectLinesManager</c> class and
/// indicates that the user has changed the default values.
/// </summary>
void clicked()
{
    super();

    purchSelectLinesManager.unMarkAll();

    selectionHasChanged = true;

    // Must call linkActive in order to force refreshing of edit method values.
    purchTable_ds.linkActive();
}

/// <summary>
/// Indicates that the user has changed the defaulted value of the included field.
/// </summary>
public void clicked()
{
    super();

    selectionHasChanged = true;

    if (this.value())
    {
        element.checkIfMiscChargesCreditDebitTypeItem();
    }

    purchTable_InvoiceAccount.setFocus();
}

/// <summary>
/// Indicates that the user has changed the defaulted value of the included field.
/// </summary>
public void clicked()
{
    super();

    lineIncludedFormObservable.markChanged();

    selectionHasChanged = true;
    element.refreshDataSources();

    purchLine_ds.active();
}

/// <summary>
/// Passes the object of <c>PurchFormLetter_Invoice</c> to the helper class <c>PurchSelectLinesManager</c>
/// for the invoice lines creation or deletion, then closes the form.
/// </summary>
void clicked()
{
    purchSelectLinesManager.passSets(localPurchFormLetter);
    super();
}

void clicked()
{
    cancelClicked = true;
    super();
}
