public class PurchTableChangeReviewWorkspace extends FormRun
{
    private PurchTableChangeReviewDataProvider provider;
    private SysInstrumentationLogger logger;
    private boolean isAIEnabled;
    private PurchCopilotFeedbackManager lowImpactFeedbackManager, highImpactFeedbackManager, downstreamImpactFeedbackManager;
    private PurchTableChangeReviewWorkspaceAIProvider aiProvider;
    private PurchTableChangeReviewImpactTreeFormControlBuilder impactedOrdersTreeControlBuilder;
    private boolean pluginExecuteErrorShown = false;

}

public void init()
{
    logger = SysInstrumentationLoggerFactory::CreateLogger(formStr(PurchTableChangeReviewWorkspace));
    provider = new PurchTableChangeReviewDataProvider();
    provider.init();
    aiProvider = PurchTableChangeReviewWorkspaceAIProvider::construct();

    super();

    isAIEnabled = PurchTableChangeReviewWorkspaceAIProviderFlight::instance().isEnabled()
        && aiProvider.isLanguageSupported()
        && aiProvider.isEnabled();

    lowImpactFeedbackManager = new PurchCopilotFeedbackManager(logger, LowImpactLike, LowImpactDislike, 'LowImpactSummary');
    highImpactFeedbackManager = new PurchCopilotFeedbackManager(logger, HighImpactLike, HighImpactDislike, 'HighImpactSummary');
    downstreamImpactFeedbackManager = new PurchCopilotFeedbackManager(logger, DownstreamImpactLike, DownstreamImpactDislike, 'DownstreamImpactSummary');

    impactedOrdersTreeControlBuilder = PurchTableChangeReviewImpactTreeFormControlBuilder::newFromControl(ImpactedOrdersTree);

    this.refreshSummary(false);
}

public void summaryCallbackLowImpact(AsyncTaskResult _result)
{
    if (!element.isClosed())
    {
        this.summaryCallback(_result, LowImpactSummary, LowImpactProgress);
    }
}

public void summaryCallbackHighImpact(AsyncTaskResult _result)
{
    if (!element.isClosed())
    {
        this.summaryCallback(_result, HighImpactSummary, HighImpactProgress);
    }
}

public void summaryCallbackDownstreamImpact(AsyncTaskResult _result)
{
    if (!element.isClosed())
    {
        this.summaryCallback(_result, DownstreamImpactSummary, DownstreamImpactProgress);
    }
}

public void summaryCallback(AsyncTaskResult _result, FormStringControl _summaryControl, FormProgressControl _progressControl)
{
    SysInfologEnumerator infologEnum = SysInfologEnumerator::newData(_result.getInfologData());
    str summary;

    while (infologEnum.moveNext())
    {
        infolog.add(infologEnum.currentException(), infologEnum.currentMessage(), infologEnum.currentHelpUrl());
    }

    [summary] = _result.getResult();

    if (!summary)
    {
        if (_result.getException() != null)
        {
            error(_result.getException().Message);
        }

        if (!this.pluginExecuteErrorShown)
        {
            this.pluginExecuteErrorShown = true;
            error("@ProcurementAndSourcing:AISummaryGenerationFailed");
        }

        summary = this.getImpactControlBasicSummary(_summaryControl);
    }

    _summaryControl.text(summary);
    _summaryControl.visible(true);
    _progressControl.visible(false);
}

private str getImpactControlBasicSummary(FormStringControl _summaryControl)
{
    str extendedSummaryLowRisk, extendedSummaryHighRisk, downstreamImpact;
    [extendedSummaryLowRisk, extendedSummaryHighRisk, downstreamImpact] = provider.getBasicSummary();

    switch(_summaryControl.name())
    {
        case DownstreamImpactSummary.name():
            DownstreamImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");
            DownstreamImpactLike.visible(false);
            DownstreamImpactDislike.visible(false);
            DownstreamTroubleshoot.visible(true);
            return downstreamImpact;
        case HighImpactSummary.name():
            HighImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");
            HighImpactLike.visible(false);
            HighImpactDislike.visible(false);
            HighImpactTroubleshoot.visible(true);
            return extendedSummaryHighRisk;
        case LowImpactSummary.name():
            LowImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");
            LowImpactLike.visible(false);
            LowImpactDislike.visible(false);
            LowImpactTroubleshoot.visible(true);
            return extendedSummaryLowRisk;
        default:
            return "";
    }
}

public void updateSummary(str _extendedSummaryLowRisk, str _extendedSummaryHighRisk, str _downstreamImpact)
{
    LowImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewLowImpactTitleWithCount", provider.getLowImpactChangeCount()));
    HighImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewHighImpactTitleWithCount", provider.getHighImpactChangeCount()));
    DownstreamImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewDownstreamImpactTitleWithCount", provider.getImpactedOrdersCount()));
    LowImpactSummary.text(_extendedSummaryLowRisk);
    HighImpactSummary.text(_extendedSummaryHighRisk);
    DownstreamImpactSummary.text(_downstreamImpact);
    LowImpactSummary.visible(true);
    HighImpactSummary.visible(true);
    DownstreamImpactSummary.visible(true);
    LowImpactProgress.visible(false);
    HighImpactProgress.visible(false);
    DownstreamImpactProgress.visible(false);
}

private void refreshSummary(boolean updateQueries)
{
    LowImpactIcon.visible(isAIEnabled);
    HighImpactIcon.visible(isAIEnabled);
    DownstreamImpactIcon.visible(isAIEnabled);

    LowImpactLikeDislike.enabled(isAIEnabled);
    HighImpactLikeDislike.enabled(isAIEnabled);
    DownstreamImpactLikeDislike.enabled(isAIEnabled);

    LowImpactLike.visible(isAIEnabled);
    LowImpactDislike.visible(isAIEnabled);
    HighImpactLike.visible(isAIEnabled);
    HighImpactDislike.visible(isAIEnabled);
    DownstreamImpactLike.visible(isAIEnabled);
    DownstreamImpactDislike.visible(isAIEnabled);

    LowImpactProgress.visible(true);
    HighImpactProgress.visible(true);
    DownstreamImpactProgress.visible(true);

    if (isAIEnabled)
    {
        this.pluginExecuteErrorShown = false;

        LowImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewLowImpactTitleWithCount", provider.getLowImpactChangeCount()));
        HighImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewHighImpactTitleWithCount", provider.getHighImpactChangeCount()));
        DownstreamImpactSummaryTitle.text(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewDownstreamImpactTitleWithCount", provider.getImpactedOrdersCount()));

        DownstreamImpactDisclaimer.text("@ProcurementAndSourcing:AIGeneratedDisclaimer");
        HighImpactDisclaimer.text("@ProcurementAndSourcing:AIGeneratedDisclaimer");
        LowImpactDisclaimer.text("@ProcurementAndSourcing:AIGeneratedDisclaimer");

        LowImpactLikeDislike.enabled(true);
        HighImpactLikeDislike.enabled(true);
        DownstreamImpactLikeDislike.enabled(true);

        aiProvider.summarizeLowImpact(element, formMethodStr(PurchTableChangeReviewWorkspace,summaryCallbackLowImpact), provider);
        aiProvider.summarizeHighImpact(element, formMethodStr(PurchTableChangeReviewWorkspace,summaryCallbackHighImpact), provider);
        aiProvider.summarizeDownstreamImpact(element, formMethodStr(PurchTableChangeReviewWorkspace,summaryCallbackDownstreamImpact), provider);
    }
    else
    {
        str extendedSummaryLowRisk, extendedSummaryHighRisk, downstreamImpact;
        [extendedSummaryLowRisk, extendedSummaryHighRisk, downstreamImpact] = provider.getBasicSummary();
        this.updateSummary(extendedSummaryLowRisk, extendedSummaryHighRisk, downstreamImpact);

        DownstreamTroubleshoot.visible(true);
        HighImpactTroubleshoot.visible(true);
        LowImpactTroubleshoot.visible(true);

        DownstreamImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");
        HighImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");
        LowImpactDisclaimer.text("@ProcurementAndSourcing:AISummaryGenerationFailed");

        MenuItemMessageAction actionData = new MenuItemMessageAction();
        actionData.MenuItemName(menuItemDisplayStr(PurchTableChangeReviewTroubleshootDialog));
        actionData.MenuItemType(MenuItemType::Display);
        Message::AddAction(MessageSeverity::Warning, "@ProcurementAndSourcing:AISummaryGenerationFailed", "@ProcurementAndSourcing:PurchTableChangeReviewTroubleshootRedirect", MessageActionType::DisplayMenuItem, FormJsonSerializer::serializeClass(actionData));
     }

    if (updateQueries)
    {
        PurchTableChangeReviewImpactedOrderTmp.setTmpData(provider.getImpactedOrdersTable());
        PurchTableChangeReviewImpactedOrderTmp_ds.executeQuery();
    }
}

