public void clicked()
{
    super();
    if (PurchTable.Email)
    {
        str name = PurchTable.contactPersonName();
        if (!name)
        {
            name = PurchTable.PurchName;
        }
        boolean useAI = isAIEnabled && Box::confirm("@ProcurementAndSourcing:AIGeneratedEmailDisclaimer");

        str content = '';

        if(useAI)
        {
            str composeMessageResponse = aiProvider.composeMessage(false, PurchTable.contactPersonName(), PurchTable.PurchId, provider);
            if(!composeMessageResponse)
            {
                error("@ProcurementAndSourcing:AIMessageGenerationFailed");
            }

            content = System.Uri::EscapeDataString(composeMessageResponse);
        }

        str subject = System.Uri::EscapeDataString(strFmt("@ProcurementAndSourcing:PurchTableChangeReviewWorkspaceEmailTitle", PurchTable.PurchId));
        str email = PurchTable.Email;
        str url = strFmt('mailto:%1?subject=%2&body=%3', email, subject, content);
        Browser b = new Browser();
        b.navigate(url);
    }
    else
    {
        warning("@ProcurementAndSourcing:PurchTableChangeReviewWorkspaceContactMissing");
    }
}

public void clicked()
{
    super();
    if (PurchTable.Email)
    {
        str name = PurchTable.contactPersonName();
        if (!name)
        {
            name = PurchTable.PurchName;
        }               
        boolean useAI = isAIEnabled && Box::confirm("@ProcurementAndSourcing:AIGeneratedMessageDisclaimer");
        str content = '';

        if (useAI)
        {
            str composeMessageResponse = aiProvider.composeMessage(false, name, PurchTable.PurchId, provider);
            if(!composeMessageResponse)
            {
                error("@ProcurementAndSourcing:AIMessageGenerationFailed");
            }
            content = System.Uri::EscapeDataString(composeMessageResponse);
        }

        str email = System.Uri::EscapeDataString(PurchTable.Email);
        str url = strFmt('https://teams.microsoft.com/l/chat/0/0?users=%1&message=%2', email, content);
        Browser b = new Browser();
        b.navigate(url);
    }
    else
    {
        warning("@ProcurementAndSourcing:PurchTableChangeReviewWorkspaceContactMissing");
    }
}

