Method.Source:
[Form]
public class PurchTableLineQuantity extends FormRun
{
    PurchTableForm  purchTableForm;
    PdsCWFormCtrl   formCtrl;
    InventDimCtrl_Frm_Mov inventDimFormSetup;

}

public void close()
{
    Object          caller = this.args().caller();
    FormRun         formRun;
    FormDataSource  formDataSource;

    super();

    if (caller)
    {
        if (caller && caller is FormRun)
        {
            formRun = caller;
            if (formRun && formRun.name() == formStr(PurchTable))
            {
                formDataSource = formRun.dataSource(tableStr(PurchLine));
                if (formDataSource)
                {
                    formDataSource.research(true);
                }
            }
        }
    }
}

/// <summary>
/// Hide quality management controls.
/// </summary>
void hideQualityManagement()
{
    inventNonConformanceTableLine.visible(false);
    inventQualityOrderTableLine.visible(false);
}

public void init()
{
    FormRun caller = this.args().caller();

    if (#PdsCatchWeightEnabled)
    {
        formCtrl = PdsCWFormCtrl::constructFromForm(this);
        formCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
            [[  fieldNum(PurchLine, PurchReceivedNow),
                fieldNum(PurchLine, PdsCWInventReceivedNow),
                true,
                fieldNum(PurchLine, PurchUnit)]] ));
        formCtrl.parmItemFieldId(fieldNum(PurchLine, ItemId));
        formCtrl.initPre();
    }

    super();

    if (!element.args().dataset())
    {
        throw error(strFmt("@SYS22678", element.name()));
    }

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }
    if (formCtrl)
    {
        formCtrl.initPost();
    }

    if (#pdsEnabled)
    {
        // Hide the control in the grid since it has been moved to a separate tab
        purchLine_PurchReceivedNow.visible(false);
    }

    purchTableForm = PurchTableForm::constructFromArgs(
        caller && (caller.name() == formStr(PurchTable)) ? caller.args() : this.args());

    purchtable_ds.linkActive();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement  movement = InventMovement::construct(purchLine);

    switch (mode)

    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

public int active()
{
    purchTableForm.purchTable(purchTable);

    return super();
}

void  linkActive()
{
    PurchTable tmpPurchTable = element.args().record();

    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(PurchTable));
    queryBuildDataSource.clearRanges();
    queryBuildDataSource.addRange(fieldNum(PurchTable, PurchId)).value(tmpPurchTable.PurchId);

    super();
}

public void init()
{
    super();

    if (formCtrl)
    {
        formCtrl.dataSourceInitPost(purchLine);
    }
    this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, IsDeleted)).value(queryValue(NoYes::No));
}

public int active()
{
    int ret = super();

    if (formCtrl)
    {
        formCtrl.dataSourceActivePost();
        if (!purchLine.purchReceivedNowAllowEdit())
        {
            purchLine_ds.object(fieldNum(PurchLine, PdsCWInventReceivedNow)).allowEdit(false);
            purchLine_ds.object(fieldNum(PurchLine, PurchReceivedNow)).allowEdit(false);
        }
    }
    else
    {
        purchLine_ds.object(fieldNum(PurchLine, PurchReceivedNow)).allowEdit(purchLine.purchReceivedNowAllowEdit());
    }
    element.EnableInventoryFields();

    return ret;
}

public void modified()
{
    super();

    if (formCtrl)
    {
        formCtrl.fieldModified(fieldNum(PurchLine, PdsCWInventReceivedNow));
    }
}

public void modified()
{
    super();

    if (formCtrl)
    {
        formCtrl.fieldModified(fieldNum(PurchLine, PurchReceivedNow));
    }
}
