void  linkActive()
{
    QueryBuildDataSource    queryBuildDataSource;
    PurchTable              tmpPurchTable;

    tmpPurchTable   =   element.args().record();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(PurchTable));
    queryBuildDataSource.clearRanges();

    queryBuildDataSource.addRange(fieldNum(PurchTable, PurchId)).value(tmpPurchTable.PurchId);

    super();
}

display DocumentDate interCompanyDocumentDate()
{
    InterCompanySalesType   salesType;
    SalesStatus             salesStatus;
    DocumentStatus          documentStatus;
    date                    documentDate;
    Num                     documentNum;

    if (!interCompanySalesCache.exists(purchTable_Reference.RecId))
    {
        interCompanySalesCache.insert(purchTable_Reference.RecId,purchTable_Reference.interCompanySalesInfo());
    }
    [salesType,salesStatus,documentStatus,documentDate,documentNum] = interCompanySalesCache.lookup(purchTable_Reference.RecId);
    return documentDate;
}

