public void executeQuery()
{
    if (purchTable.InterCompanySalesId)
    {
        this.query().dataSourceTable(tableNum(PurchTable)).findRange(fieldNum(PurchTable,PurchId)).value(purchTable.PurchId);
        super();
    }
    else
    {
        purchTable_Reference.clear();
    }
}

