public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOptionHeader = OfficeGeneratedExportMenuItem::construct(tableStr(PurchaseOrderResponseHeaderEntity), ExportHeadersToExcelOptionId);
    exportOptionHeader.displayName(strfmt("@SYS76681", "@SCM:DataEntity_PurchaseOrderResponseHeaderEntity", '', curExt()));
    exportOptions.addEnd(exportOptionHeader);

    var exportOptionHeaderAndLines = OfficeGeneratedExportMenuItem::construct(tableStr(PurchaseOrderResponseLineEntity), ExportHeaderAndLinesToExcelOptionId);
    exportOptionHeaderAndLines.displayName(strfmt("@SYS76681", "@SCM:PurchaseOrderResponseHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOptionHeaderAndLines);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportHeadersToExcelOptionId:
            context = this.exportHeadersDataEntityContext();
            break;

        case ExportHeaderAndLinesToExcelOptionId:
            context = this.exportHeaderAndLinesDataEntityContext();
            break;
    }

    return context;
}

