private void showHideLatestVersionInfoAndAction()
{
    boolean isLatestVersion =
            PurchaseOrderResponseVersion::find(PurchaseOrderResponseHeaderAllVersions.PurchaseOrderResponseVersion)
            .isLatest();

    PurchVendorPortalViewLatestResponse.visible(!isLatestVersion);

    boolean enablePurchaseOrderResponseConsuption = isLatestVersion
                                                    && !PurchaseOrderResponseHeaderAllVersions.isResponseHeaderAndLinesProcessed()
                                                    && PurchaseOrderResponseHeaderAllVersions.ResponseState == PurchaseOrderResponseState::AcknowledgedWithChange;
    PurchaseOrderResponseConsume.enabled(enablePurchaseOrderResponseConsuption);
    PurchaseOrderResponseConsumeLine.enabled(enablePurchaseOrderResponseConsuption);

    if (isLatestVersion)
    {
        element.design().caption("@SCM:PurchaseOrderResponse");
    }
    else
    {
        element.design().caption(strFmt("%1 - %2","@SCM:PurchaseOrderResponse", "@SCM:Outdated"));
    }
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
private void updateDesign(InventDimFormDesignUpdate _mode)
{
    // Set up UI control for inventory dimension controls on line grid
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                InventDimParm inventDimParmLocal;
                inventDimFormSetup = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventDimParmLocal.setAllProductDimensions();
                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private void registerForChangeTracking()
{
    viewManager = VersioningDocumentViewManager::construct();
    viewManager.registerDataContainerForDataSource(
        PurchaseOrderResponseHeaderAllVersions_DS,
        VersioningDocumentViewDataContainer::constructFromDataSource(
            PurchaseOrderResponseHeaderAllVersions_DS,
            [fieldStr(PurchaseOrderResponseHeaderAllVersions, PurchTableVersion)],
            fieldStr(PurchaseOrderResponseHeaderAllVersions, VersionDateTime)));
    viewManager.registerDataContainerForDataSource(
        PurchaseOrderResponseLineAllVersions_DS,
        VersioningDocumentViewDataContainer::constructFromDataSource(
            PurchaseOrderResponseLineAllVersions_DS,
            [fieldStr(PurchaseOrderResponseLineAllVersions, PurchTableVersion),
            fieldStr(PurchaseOrderResponseLineAllVersions, PurchaseOrderResponseLine)],
            fieldStr(PurchaseOrderResponseLineAllVersions, VersionDateTime)));
    viewmanager.registerControlForTracking(PurchaseOrderResponseHeaderAllVersions_DlvTerm);
    viewmanager.registerControlForTracking(PurchaseOrderResponseHeaderAllVersions_DlvMode);
    viewManager.registerControlForTracking(PurchaseOrderResponseHeaderArchivedVersions_VendorRef);
    viewManager.registerControlForTracking(PurchaseOrderResponseLineAllVersions_PurchQty);
    viewManager.registerControlForTracking(PurchaseOrderResponseLineAllVersions_ConfirmedDlv);
    viewManager.registerControlForTracking(PurchaseOrderResponseLineAllVersions_ExternalItemId);
    viewManager.registerControlForTracking(PurchaseOrderResponseLineAllVersions_Name);
}

