private boolean checkSelectedLinesAreNotConsumed()
{
    MultiSelectionHelper selectionHelper = MultiSelectionHelper::construct();
    selectionHelper.parmDatasource(PurchaseOrderResponseLineAllVersions_ds);
    PurchaseOrderResponseLineAllVersions selectedResponseLine = selectionHelper.getFirst();
    while (selectedResponseLine)
    {
        if (selectedResponseLine.ConsumptionState == ConsumptionState::NotConsumed)
        {
            return true;
        }
        selectedResponseLine = selectionHelper.getNext();
    }
    return false;
}

private void refreshCallerForm()
{
    if (this.args() != null && this.args().caller() != null)
    {
        FormRun callerForm = this.args().caller();
        callerForm.dataSource().research(true);
    }
}

private ExportToExcelDataEntityContext exportHeadersDataEntityContext()
{
    const var headerEntityName = tableStr(PurchaseOrderResponseHeaderEntity);

    var context = ExportToExcelDataEntityContext::constructDefault(headerEntityName);

    var headerEntityInfo = context.findEntityByName(headerEntityName);
    var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);
    var headerFilter = headerFilterBuilder.companyFilter();
    headerEntityInfo.filter(headerFilter);

    return context;
}

private ExportToExcelDataEntityContext exportHeaderAndLinesDataEntityContext()
{
    const var headerEntityName = tableStr(PurchaseOrderResponseHeaderEntity);
    const var lineEntityName = tableStr(PurchaseOrderResponseLineEntity);

    var context = ExportToExcelDataEntityContext::constructDefault(headerEntityName);

    var headerEntityInfo = context.findEntityByName(headerEntityName);
    var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);
    var headerFilter = headerFilterBuilder.and(
        headerFilterBuilder.companyFilter(),
        headerFilterBuilder.and(
            headerFilterBuilder.areEqual(fieldStr(PurchaseOrderResponseHeaderEntity, PurchaseOrderNumber), PurchaseOrderResponseHeaderAllVersions.PurchId),
            headerFilterBuilder.areEqual(fieldStr(PurchaseOrderResponseHeaderEntity, PurchaseOrderResponseLastModificationDateTime), PurchaseOrderResponseHeaderAllVersions.VersionDateTime)
        )
    );
    headerEntityInfo.filter(headerFilter);

    var lineEntityInfo = context.addEntityDefault(lineEntityName);
    var lineFilterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);
    var lineFilter = lineFilterBuilder.and(
        lineFilterBuilder.companyFilter(),
        lineFilterBuilder.and(
            lineFilterBuilder.areEqual(fieldStr(PurchaseOrderResponseLineEntity, PurchaseOrderNumber), PurchaseOrderResponseHeaderAllVersions.PurchId),
            lineFilterBuilder.areEqual(fieldStr(PurchaseOrderResponseLineEntity, PurchaseOrderResponseLastModificationDateTime), PurchaseOrderResponseHeaderAllVersions.VersionDateTime)
        )
    );
    lineEntityInfo.filter(lineFilter);

    return context;
}

public void init()
{
    super();

    if (element.args().record() && element.args().record().TableId == tableNum(PurchaseOrderResponseHeaderAllVersions) && element.args().menuItemName() != menuItemDisplayStr(PurchVendorPortalPendingResponses))
    {
        PurchaseOrderResponseHeaderAllVersions responseHeader = element.args().record();

        QueryBuildDataSource qbds = this.query().dataSourceTable(this.table());
        qbds.addRange(fieldNum(PurchaseOrderResponseHeaderAllVersions, PurchaseOrderResponseVersion))
            .value(queryValue(responseHeader.PurchaseOrderResponseVersion));

        element.updateDesignForSingleRecord();
    }

    LogisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    LogisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                  DateTimeUtil::maxValue());
}

