public void clicked()
{
    if (!element.checkSelectedLinesAreNotConsumed())
    {
        throw error("@SCM:PurchaseOrderResponseConsumeLine_AlreadyConsumed");
    }
    setPrefix("@SCM:PurchaseOrderResponseConsumeDetails");

    super();

    element.refreshCallerForm();
