public class PurchVendorPortalOriginalOrder extends FormRun implements SysIFilterConsumerForm
{
    InventDimCtrl_Frm_Setup     inventDimFormSetup;
    boolean                     isPurchTableVersionArchived;
    boolean                     retainPosition;
    int                         position;

}

private void retainPosition()
{
    position = PurchaseOrderResponseOriginalOrderVersion_ds.getPosition();
    retainPosition = true;
}

private void setPosition()
{
    if (retainPosition)
    {
        if (position <= PurchaseOrderResponseOriginalOrderVersion_DS.numberOfRowsLoaded())
        {
            PurchaseOrderResponseOriginalOrderVersion_DS.setPosition(position);
        }
        else
        {
            PurchaseOrderResponseOriginalOrderVersion_DS.last();
        }

        retainPosition = false;
    }
}

private void setupViewResponseControlLabel(PurchaseOrderResponseState _purchaseOrderResponseState)
{
    if (_purchaseOrderResponseState == PurchaseOrderResponseState::Editing)
    {
        PurchVendorPortalViewResponse.caption("@SCM:ShowChanges");
    }
    else if (_purchaseOrderResponseState == PurchaseOrderResponseState::Confirmed || _purchaseOrderResponseState == PurchaseOrderResponseState::Rejected || _purchaseOrderResponseState == PurchaseOrderResponseState::AcknowledgedWithChange)
    {
        PurchVendorPortalViewResponse.caption("@SCM:ShowResponse");
    }
}

private void updateFormForResponseStateEditing(PurchaseOrderResponseState _responseState, boolean _isPartOfLatestResponse)
{
    boolean responseStateIsEditing = _responseState == PurchaseOrderResponseState::Editing;
    boolean responseStateIsPending = _responseState == PurchaseOrderResponseState::Pending;

    this.showHideControlsForEditMode(responseStateIsEditing, _isPartOfLatestResponse, responseStateIsPending);
    this.showHideControlsForNonEditMode(responseStateIsPending && _isPartOfLatestResponse);
    this.setupViewResponseControlLabel(_responseState);

    PurchVendorPortalViewVendorResponse.visible(!responseStateIsEditing && !responseStateIsPending);
}

private void showHideControlsForEditMode(boolean _showControl, boolean _isPartOfLatestResponse, boolean _responseStateIsPending)
{
    PurchaseOrderResponseAcknowledgeChange.visible(_showControl && _isPartOfLatestResponse);
    PurchVendorPortalViewResponse.visible(!_responseStateIsPending);
}

private void showHideControlsForNonEditMode(boolean _showControl)
{
    PurchaseOrderResponseConfirm.visible(_showControl);
    PurchaseOrderResponseReject.visible(_showControl);
    PurchVendorPortalEditResponse.visible(_showControl);
}

private void showHidePricesAndDiscounts()
{
    boolean showHidePriceAndDiscount = PurchVendorPortalPricesViewManager::showHidePrices(PurchaseOrderResponseHeaderAllVersions.OrderAccount);
    element.showHideHeaderPriceAndDiscount(showHidePriceAndDiscount);
    element.showHideLinePriceAndDiscount(showHidePriceAndDiscount);
}

private void showHideHeaderPriceAndDiscount(NoYes _showPriceAndDiscount)
{
    PurchConfirmationRequestJour_Amount.visible(_showPriceAndDiscount);
    PurchaseOrderResponseHeaderAllVersions_DiscPercent.visible(_showPriceAndDiscount);
    PurchaseOrderResponseHeaderAllVersions_CurrencyCode.visible(_showPriceAndDiscount);
    ShowHeaderCharges.visible(_showPriceAndDiscount);
}

private void showHideLinePriceAndDiscount(NoYes _showPriceAndDiscount)
{
    PurchaseOrderResponseLineAllVersions_LineAmount.visible(_showPriceAndDiscount);
    PurchaseOrderResponseLineAllVersions_LineDisc.visible(_showPriceAndDiscount);
    PurchaseOrderResponseLineAllVersions_LinePercent.visible(_showPriceAndDiscount);
    PurchaseOrderResponseLineAllVersions_PurchPrice.visible(_showPriceAndDiscount);
    PurchaseOrderResponseLineAllVersions_PriceUnit.visible(_showPriceAndDiscount);
    LineCharges.visible(_showPriceAndDiscount);
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
private void updateDesign(InventDimFormDesignUpdate _mode)
{
    // Set up UI control for inventory dimension controls on line grid
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                InventDimParm inventDimParmLocal;
                inventDimFormSetup = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventDimParmLocal.setAllProductDimensions();
                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Filters the datasource to the first response for the given request - which is essentially the same as the PO request.
/// </summary>
/// <param name = "_purchTableVersion">
/// RecId of <c>PurchTableVersion</c> record for which the response should be found.
/// </param>
private void filterDataSourceToFirstResponse(PurchTableVersionRecId _purchTableVersion)
{
    SysQuery::findOrCreateRange(PurchaseOrderResponseOriginalOrderVersion_DS.queryBuildDataSource(), fieldNum(PurchaseOrderResponseOriginalOrderVersion, PurchaseOrderResponseVersion))
                .value(queryValue(PurchTableVersion::find(_purchTableVersion).findFirstResponseVersion().RecId));
}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    VendTable_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

