public void init()
{
    #SysSystemDefinedButtons

    VendVendorPortalUserRequestManager::validateVendorPortalUserAccessToAtLeastOneVendor(this);

    super();
    // Hide attach button
    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (attachButton)
    {
        attachButton.visible(false);
    }

    if (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalOriginalOrder))
    {
        NavList.visible(false);
    }

    if (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalBindingConfRequestJournal))
    {
        element.design().caption("@SCM:SentPurchaseOrders");
    }

    // Standard pattern for controlling and showing inventory dimensions on a grid line
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

public void init()
{
    super();

    if ((element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalOriginalOrderList))
        || (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalOrdersForReview)))
    {
        QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(PurchaseOrderResponseOriginalOrderVersion));
        qbds.clearDynalinks();
    }
}

public void executeQuery()
{
    if (element.args().record().TableId == tableNum(PurchaseOrderResponseHeaderAllVersions))
    {
        PurchaseOrderResponseHeaderAllVersions responseHeader = element.args().record();

        element.filterDataSourceToFirstResponse(responseHeader.PurchTableVersion);
    }
    else if (element.args().record().TableId == tableNum(PurchaseOrderResponseHeader))
    {
        PurchaseOrderResponseHeader responseHeader = element.args().record();

        element.filterDataSourceToFirstResponse(responseHeader.findLatestResponseVersion().PurchTableVersion);
    }

    super();

    element.setPosition();
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(this.table());
    QueryBuildDataSource qbdsPurchTable = qbds.addDataSource(tableNum(PurchTable));
    qbds.addLink(fieldNum(PurchaseOrderResponseOriginalOrderVersion, PurchaseOrderResponseVersion), fieldNum(PurchaseOrderResponseHeaderAllVersions, PurchaseOrderResponseVersion));

    qbdsPurchTable.addLink(fieldNum(PurchaseOrderResponseHeaderAllVersions, PurchId), fieldNum(PurchTable, PurchId));
    QueryBuildRange purchStatusRange = qbdsPurchTable.addRange(fieldnum(PurchTable, PurchStatus));
    str purchStatusRangeValue = strfmt('((%1.%2 != %3) || ((%4.%2 == %3) && (%1.%5 == %6)))',
                qbdsPurchTable.name(),
                fieldstr(PurchTable, PurchStatus),
                enum2int(PurchStatus::Canceled),
                qbds.name(),
                fieldstr(PurchTable, DocumentState),
                enum2int(Versioningdocumentstate::InExternalReview));
    purchStatusRange.value(purchStatusRangeValue);

    if (element.args().record().TableId == tableNum(PurchaseOrderResponseHeaderSummary))
    {
        PurchaseOrderResponseHeaderSummary summary = element.args().record();

        qbds.addRange(fieldNum(PurchaseOrderResponseHeaderAllVersions, PurchTableVersion))
            .value(queryValue(summary.PurchTableVersion));
        qbds.addRange(fieldNum(PurchaseOrderResponseHeaderAllVersions, VersionDateTime))
            .value(queryValue(summary.ReceivedDateTime));
    }

    LogisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    LogisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                  DateTimeUtil::maxValue());

}

public int active()
{
    int ret;

    ret = super();

    isPurchTableVersionArchived = PurchTableVersion::find(PurchaseOrderResponseHeaderAllVersions.PurchTableVersion).IsArchived;

    element.updateFormForResponseStateEditing(PurchaseOrderResponseOriginalOrderVersion.responseState(),
                                            PurchaseOrderResponseHeaderAllVersions.isPartOfLatestResponse());

    element.showHidePricesAndDiscounts();

    return ret;
}

