public void init()
{
    super();

    // Set up join from order header to vendor
    var queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearLinks();

    queryBuildDataSource.addLink(fieldnum(PurchaseOrderResponseHeaderAllVersions, OrderAccount), fieldnum(VendTable, AccountNum));

    VendIVendorFilterProvider vendorFilterProvider = element.args().caller() as VendIVendorFilterProvider;

    if (vendorFilterProvider)
    {
        queryBuildDataSource.addRange(fieldNum(VendTable, AccountNum)).value(queryValue(vendorFilterProvider.parmVendorFilter()));
    }
}

public void init()
{
    super();

    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.addLink(fieldnum(PurchaseOrderResponseOriginalOrderVersion, PurchTableVersion), fieldnum(PurchaseOrderResponseHeaderSummary, PurchTableVersion));

    if (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalOrdersForReview))
    {
        queryBuildDataSource.addRange(fieldNum(PurchaseOrderResponseHeaderSummary, DocumentState))
                            .value(strFmt("(%1.%2 < %3)", tableStr(PurchaseOrderResponseHeaderSummary), fieldStr(PurchaseOrderResponseHeaderSummary, DocumentState), int2Str(VersioningDocumentState::Confirmed)));
    }
}

public void init()
{
    super();
    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(PurchaseOrderResponseVersion));
    queryBuildDataSource.addLink(fieldNum(PurchaseOrderResponseHeaderSummary, PurchTableVersion), fieldNum(PurchaseOrderResponseVersion, PurchTableVersion));
    queryBuildDataSource.addLink(fieldNum(PurchaseOrderResponseHeaderSummary, RespondedDateTime), fieldNum(PurchaseOrderResponseVersion, VersionDateTime));

    if (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalOrdersForReview))
    {
        container responseStateContainer;
        responseStateContainer += queryValue(PurchaseOrderResponseState::Pending);
        responseStateContainer += queryValue(PurchaseOrderResponseState::Editing);

        queryBuildDataSource.addRange(fieldNum(PurchaseOrderResponseVersion, ResponseState)).value(con2Str(responseStateContainer));
    }
}

