public void clicked()
{
    if (isPurchTableVersionArchived)
    {
        super();
    }
    else
    {
        Args    args;
        FormRun formRun;
        args = new Args(formStr(MarkupTrans));
        args.caller(element);
        args.record(PurchTable::find(PurchaseOrderResponseHeaderAllVersions.PurchId));
        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

public void clicked()
{
    Args args = new Args();
    if (PurchaseOrderResponseHeaderAllVersions.IsArchived)
    {
        args.record(PurchaseOrderResponseHeaderHistory::find(PurchaseOrderResponseHeaderAllVersions.ResponseHeader));
    }
    else
    {
        args.record(PurchaseOrderResponseHeader::find(PurchaseOrderResponseHeaderAllVersions.ResponseHeader));
    }
    new MenuFunction(menuItemDisplayStr(DocuAttachment), MenuItemType::Display).run(args);
}

