[Form]
public class PurchVendorPortalRequests extends FormRun implements SysIFilterConsumerForm
{
    InventDimCtrl_Frm_Setup     inventDimFormSetup;
    PurchTable                  purchTableArgs;

}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PurchTableAllVersions_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
private void updateDesign(InventDimFormDesignUpdate _mode)
{
    // Set up UI control for inventory dimension controls on line grid
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                InventDimParm inventDimParmLocal;
                inventDimFormSetup = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventDimParmLocal.setAllProductDimensions();
                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void init()
{
    if (element.args().dataset() == tableNum(PurchTable))
    {
        purchTableArgs = element.args().record();
    }

    super();

    // Set a name for the form defined query to allow the filter strategy to be detected
    PurchTableAllVersions_ds.query().name(element.name());

    // Standard pattern for controlling and showing inventory dimensions on a grid line
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

/// <summary>
/// Initialization of the purchase order version data source
/// </summary>
public void init()
{
    super();

    // If form was called with a purchase order apply a filter on that
    if (purchTableArgs)
    {
        var qbDS = this.query().dataSourceTable(this.table());
        var range = qbDS.addRange(fieldNum(PurchTableAllVersions, PurchId));
        range.value(queryValue(purchTableArgs.PurchId));
    }
}

/// <summary>
/// Initialization of the purchase order version lines data source
/// </summary>
public void init()
{
    super();

    // Set up join from version lines to version header
    var qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldnum(PurchLineAllVersions, PurchTableVersionRecId), purchTableAllVersions, fieldnum(PurchTableAllVersions, PurchTableVersionRecId));

    // Filter to avoid showing purchase order commercial lines (delivery schedule) on the vendor portal
    var rangeLineDeliveryType = qbDS.addRange(fieldNum(PurchLineAllVersions, LineDeliveryType));
    rangeLineDeliveryType.value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
}

/// <summary>
/// Initialization of the purchase confirmation request data source
/// </summary>
public void init()
{
    super();

    // Set up join from version header to purchase inquiry journal
    var qbDS = this.query().dataSourceTable(this.table());
    qbDS.addLink(fieldnum(PurchTableAllVersions, PurchTableVersionRecId), fieldnum(PurchConfirmationRequestJour, PurchTableVersion));

    // Filter to only shown purchase inquiries that were created while the vendor was set as enabled for vendor portal
    var rangeCollaborationType = qbDS.addRange(fieldNum(PurchConfirmationRequestJour, VendVendorCollaborationType));
    rangeCollaborationType.value(SysQuery::valueNot(VendVendorCollaborationType::Disabled));
}

/// <summary>
/// Returns the state of the confirmation request response.
/// </summary>
display public PurchaseOrderResponseState responseState(PurchConfirmationRequestJour _purchConfirmationRequestJour)
{
    return PurchTableVersion::find(_purchConfirmationRequestJour.PurchTableVersion).findLatestResponseVersion().ResponseState;
}

