public void init()
{
    super();

    var qbDS = this.query().dataSourceTable(this.table());

    qbDS.addLink(fieldnum(PurchTableAllVersions, PurchTableVersionRecId), fieldnum(PurchaseOrderResponseLastVersion, PurchTableVersion));
}

public void init()
{
    super();

    QueryBuildDataSource qbDS = this.query().dataSourceTable(this.table());

    qbDS.addLink(fieldnum(PurchaseOrderResponseLastVersion, PurchTableVersion), fieldnum(PurchaseOrderResponseVersion, PurchTableVersion));

    qbDS.addLink(fieldnum(PurchaseOrderResponseLastVersion, VersionDateTime), fieldnum(PurchaseOrderResponseVersion, VersionDateTime));
}

