public void clicked()
{
    if (PurchTableAllVersions.IsArchived)
    {
        super();
    }
    else
    {
        Args    args;
        FormRun formRun;
        args = new Args(formStr(MarkupTrans));
        args.caller(element);
        args.record(PurchTable::find(PurchTableAllVersions.PurchId));
        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

