/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PurchaseOrderResponseHeader_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public void revertChanges()
{
    if (Box::confirm("@SCM:RevertResponseConfirm"))
    {
        PurchaseOrderResponseHeader.revertToPreviousVersion();
        element.refreshCallerFormAndCloseCurrentForm();
    }
}

    private void refreshCallerFormAndCloseCurrentForm()
    {
        FormRun callerForm = element.args().caller() as FormRun;
        if (callerForm)
        {
            if (formHasMethod(callerForm, identifierStr(refreshFormDataSource)))
            {
                callerForm.refreshFormDataSource();
            }
            else
            {
                callerForm.doRefresh();
            }
        }
        element.close();
	}

public VersioningDocumentViewManager getViewManager()
{
    return viewManager;
}

private void updateFormForEdit(PurchaseOrderResponseState _responseState, boolean _forceEdit = false)
{
    boolean responseStateIsEditing = _responseState == PurchaseOrderResponseState::Editing;

    element.design().viewEditMode(responseStateIsEditing ? (_forceEdit ? ViewEditMode::Edit : formViewEditMode) : ViewEditMode::View);
    this.showHideControlsForEditMode(responseStateIsEditing);
}

private void showHideControlsForEditMode(boolean _showControl)
{
    RevertChangesButton.visible(_showControl);
    PurchaseOrderResponseAcknowledgeChange.visible(_showControl);
}

/// <summary>
/// Method for controlling and showing line's actions and fields
/// </summary>
private void updateLineForEdit()
{
    boolean isFormInEditMode = formViewEditMode == ViewEditMode::Edit;
    boolean isCWItem = PdsGlobal::pdsIsCWItem(PurchaseOrderResponseLine.ItemId);

    PurchaseOrderResponseLine_DS.allowEdit(
        isFormInEditMode &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Rejected) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Substituted) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SplitOriginalLine) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SubstituteSplit));
    LineRevert.enabled(
        isFormInEditMode &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Accepted) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Substitute) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SplitScheduleLine) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SubstituteSplit));
    LineReject.enabled(
        isFormInEditMode &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Rejected) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Substitute) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SplitScheduleLine) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::SubstituteSplit));
    LineSubstitute.enabled(
        isFormInEditMode &&
        (PurchaseOrderResponseLine.LineState == PurchaseOrderResponseLineState::Accepted ||
        PurchaseOrderResponseLine.LineState == PurchaseOrderResponseLineState::AcceptedWithChanges ||
        PurchaseOrderResponseLine.LineState == PurchaseOrderResponseLineState::SplitOriginalLine ||
        PurchaseOrderResponseLine.LineState == PurchaseOrderResponseLineState::Rejected));
    LineSplit.enabled(
        isFormInEditMode &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Rejected) &&
        (PurchaseOrderResponseLine.LineState != PurchaseOrderResponseLineState::Substituted) &&
        !isCWItem);
    PurchaseOrderResponseLine_ExternalItemId.allowEdit(this.shouldEditName());
    PurchaseOrderResponseLine_Name.allowEdit(this.shouldEditName());
    PurchaseOrderResponseLine_PurchQty.allowEdit(!isCWItem);
    ItemName.allowEdit(this.shouldEditName());
}

private boolean shouldEditName()
{
    return ((PurchaseOrderResponseLine.ItemId == "") && (PurchaseOrderResponseLine.ProcurementCategory == 0));
}

private void registerForChangeTracking()
{
    viewManager = VersioningDocumentViewManager::construct();
    viewManager.registerDataContainerForDataSource(PurchaseOrderResponseHeader_DS,
        VersioningDocumentViewDataContainer::constructFromDataSource(
            PurchaseOrderResponseHeader_DS,
            [fieldStr(PurchaseOrderResponseHeaderAllVersions, PurchTableVersion)],
            fieldStr(PurchaseOrderResponseHeaderAllVersions, VersionDateTime),
            new PurchaseOrderResponseHeaderMapper()));
    viewManager.registerDataContainerForDataSource(PurchaseOrderResponseLine_DS,
        VersioningDocumentViewDataContainer::constructFromDataSource(
            PurchaseOrderResponseLine_DS,
            [fieldStr(PurchaseOrderResponseLineAllVersions, PurchTableVersion),
             fieldStr(PurchaseOrderResponseLineAllVersions, PurchaseOrderResponseLine)],
            fieldStr(PurchaseOrderResponseLineAllVersions, VersionDateTime),
            new PurchaseOrderResponseLineMapper()));
    viewManager.registerControlForTracking(PurchaseOrderResponseHeader_DlvTerm);
    viewManager.registerControlForTracking(PurchaseOrderResponseHeader_DlvMode);
    viewManager.registerControlForTracking(PurchaseOrderResponseHeader_VendorRef);
    viewManager.registerControlForTracking(PurchaseOrderResponseLine_PurchQty);
    viewManager.registerControlForTracking(PurchaseOrderResponseLine_ConfirmedDlv);
    viewManager.registerControlForTracking(PurchaseOrderResponseLine_ExternalItemId);
    viewManager.registerControlForTracking(PurchaseOrderResponseLine_Name);
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
private void updateDesign(InventDimFormDesignUpdate _mode)
{
    // Set up UI control for inventory dimension controls on line grid
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                InventDimParm inventDimParmLocal;
                inventDimFormSetup = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventDimParmLocal.setAllProductDimensions();
                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private void updateDesignForSingleRecord()
{
    NavList.visible(false);
    element.design().caption("@SCM:PurchaseOrderResponse");
}

public void setEditModeStatusAndUpdateActionsOnForm()
{
    formViewEditMode = ViewEditMode::Edit;
    element.updateLineForEdit();
}

public void setViewModeStatusAndUpdateActionsOnForm()
{
    formViewEditMode = ViewEditMode::View;
    element.updateLineForEdit();
}

/// <summary>
/// Standard pattern method for controlling and showing inventory dimensions on a grid line
/// </summary>
public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

    public void init()
    {
        #SysSystemDefinedButtons

        super();

        // Hide attach button
        FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
        if (attachButton)
        {
            attachButton.visible(false);
        }

        // Standard pattern for controlling and showing inventory dimensions on a grid line
        element.updateDesign(InventDimFormDesignUpdate::Init);

        if (element.args().menuItemName() == menuItemDisplayStr(PurchVendorPortalNotArchivedResponse))
        {
            this.updateDesignForSingleRecord();
        }

        if ((element.args().parmEnumType() == enumNum(ViewEditMode)) &&
            (element.args().parmEnum()     == ViewEditMode::Edit))
        {
            element.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
            formViewEditMode = ViewEditMode::Edit;
        }
        else
        {
			formViewEditMode = ViewEditMode::Auto;
        }

        // subscribe to the user actions switching between view and edit modes
        element.viewEditModeHelper().EditModeSwitched += eventhandler(element.setEditModeStatusAndUpdateActionsOnForm);
        element.viewEditModeHelper().ViewModeSwitched += eventhandler(element.setViewModeStatusAndUpdateActionsOnForm);

        this.registerForChangeTracking();
    }

private boolean checkForNewPurchaseOrderVersion(FormDataSource _validatingRecord)
{
    PurchaseOrderResponseVersion latestResponseVersion = PurchaseOrderResponseHeader.findLatestResponseVersion();

    if (latestResponseVersion &&
        (latestResponseVersion.PurchTableVersion != PurchaseOrderResponseVersion.PurchTableVersion))
    {
        if (Box::okCancel("@SCM:PurchaseOrderResponseRefreshVersionConfirm", DialogButton::Cancel) ==
                DialogButton::Cancel)
        {
            return false;
        }

        _validatingRecord.reread();
        element.refreshCallerFormAndCloseCurrentForm();
    }

    return true;
}

public void init()
{
    super();

    var queryBuildDataSource = this.query().dataSourceTable(this.table());

    if (element.args().menuItemName() != menuItemDisplayStr(PurchVendorPortalNotArchivedResponse))
    {
        queryBuildDataSource.clearDynalinks();
    }
}

