public void init()
{
    super();

    container responseStateContainer;
    responseStateContainer += queryValue(PurchaseOrderResponseState::Pending);
    responseStateContainer += queryValue(PurchaseOrderResponseState::Editing);

    var queryBuildDataSource = this.query().dataSourceTable(this.table());
    var rangeResponseState = queryBuildDataSource.addRange(fieldNum(PurchaseOrderResponseVersion, ResponseState));
    rangeResponseState.value(con2Str(responseStateContainer));

    queryBuildDataSource.addRange(fieldNum(PurchaseOrderResponseVersion, IsArchived)).value(queryValue(NoYes::No));
}

