public void init()
{
    super();

    var queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.addLink(fieldnum(PurchaseOrderResponseVersion, PurchTableVersion), fieldnum(PurchConfirmationRequestJour, PurchTableVersion));
    queryBuildDataSource.addSortField(fieldNum(PurchConfirmationRequestJour, DocumentNumber), SortOrder::Ascending);

    // Filter to only show journals that were created while the vendor was set as enabled for vendor portal
    var rangeCollaborationType = queryBuildDataSource.addRange(fieldNum(PurchConfirmationRequestJour, VendVendorCollaborationType));
    rangeCollaborationType.value(SysQuery::valueNot(VendVendorCollaborationType::Disabled));
}

