public void init()
{
    super();

    // Set up join from response lines to vendor
    var queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearLinks();

    queryBuildDataSource.addLink(fieldnum(PurchaseOrderResponseHeader, OrderAccount), fieldnum(VendTable, AccountNum));

    VendIVendorFilterProvider vendorFilterProvider = element.args().caller() as VendIVendorFilterProvider;

    if (vendorFilterProvider)
    {
        queryBuildDataSource.addRange(fieldNum(VendTable, AccountNum)).value(queryValue(vendorFilterProvider.parmVendorFilter()));
    }
}

public void init()
{
    super();
    var queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.addLink(fieldnum(PurchaseOrderResponseHeader, PurchId), fieldnum(PurchTable, PurchId));
    queryBuildDataSource.addRange(fieldNum(PurchTable, DocumentState))
        .value(strFmt("(%1.%2 < %3)", tableStr(PurchTable), fieldStr(PurchTable, DocumentState), int2Str(VersioningDocumentState::Confirmed)));
}

