Method.Source:
[Form]
public class RAssetComponents extends FormRun implements RAssetIComponents
{
    public InventDimCtrl_Frm_Scrap_RU inventDimFrmComp;
    public InventDimCtrl_Frm_Scrap_RU inventDimFrmScrap;
    public RAssetTable rAssetTable;

}

public void allowCreateRAssetComponents()
{
    boolean enable;

    switch (rAssetTable.Status)
    {
        case RAssetStatus::NoAcquisition:
            enable = true;
            break;

        case RAssetStatus::PurchInvoice:
        case RAssetStatus::Scrapped:
        case RAssetStatus::Sold:
        case RAssetStatus::SoldInvoice:
        case RAssetStatus::Disposed:
        case RAssetStatus::Closed:
            enable = false;
            break;

        default:
            // RAssetStatus::Open, Lended, Suspended
            enable = RAssetParameters::find().MultiAcq;
            break;
    }

    rAssetComponents_ds.allowCreate(enable && rAssetComponents.checkComponentCreating());
}

public void init()
{
    super();

    rAssetTable = element.args().record();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    this.setEnableDisable();
}

public Object inventDimSetupObject()
{
    Object ret;

    if (this.selectedControl().name() == inventDimParmFixedScrap.name())
    {
        ret = inventDimFrmScrap;
    }
    else
    {
        ret = inventDimFrmComp;
    }

    return ret;
}

public FormDataSource rAssetComponents_ds()
{
    return rAssetComponents_ds;
}

public RAssetTable rAssetTable()
{
    return rAssetTable;
}

public void allowComponents(boolean _enable)
{
    rassetComponents_ds.allowEdit(_enable);
    rassetComponents_ds.allowCreate(_enable);
    rassetComponents_ds.allowDelete(_enable);
    inventDimComponents_ds.allowEdit(_enable);
    addComponentsFromPurch.enabled(_enable);
}

public void allowScraps(boolean _enable)
{
    rassetScraps_ds.allowEdit(_enable);
    rassetScraps_ds.allowCreate(_enable);
    rassetScraps_ds.allowDelete(_enable);
    inventDimScrap_ds.allowEdit(_enable);
    ScrapCalcPrice.enabled(_enable);
}

public void setEnableDisable()
{
    switch (rAssetTable.Status)
    {
        case RAssetStatus::NoAcquisition:
            this.allowComponents(true);
            this.allowScraps(false);
            addComponentsToScraps.enabled(false);
            break;

        case RAssetStatus::PurchInvoice:
        case RAssetStatus::Scrapped:
        case RAssetStatus::Sold:
        case RAssetStatus::SoldInvoice:
        case RAssetStatus::Disposed:
            this.allowComponents(false);
            this.allowScraps(false);
            addComponentsToScraps.enabled(false);
            break;

        case RAssetStatus::Closed:
            this.allowComponents(false);
            this.allowScraps(true);
            rassetScraps_ds.allowEdit(rassetScraps.TransDate == dateNull());
            rassetScraps_ds.allowDelete(rassetScraps.TransDate == dateNull());
            inventDimScrap_ds.allowEdit(rassetScraps.TransDate == dateNull());
            addComponentsToScraps.enabled(rAssetComponents_ds.anyMarked() || rassetComponents.TransDate != dateNull());
            break;

        default:
            // RAssetStatus::Open, Lended, Suspended
            if (RAssetParameters::find().MultiAcq == NoYes::Yes)
            {
                this.allowComponents(true);
                rassetComponents_ds.allowEdit(rassetComponents.TransDate == dateNull());
                rassetComponents_ds.allowDelete(rassetComponents.TransDate == dateNull());
                inventDimComponents_ds.allowEdit(rassetComponents.TransDate == dateNull());
            }
            else
            {
                this.allowComponents(false);
            }

            this.allowScraps(true);
            rassetScraps_ds.allowEdit(rassetScraps.TransDate == dateNull());
            rassetScraps_ds.allowDelete(rassetScraps.TransDate == dateNull());
            inventDimScrap_ds.allowEdit(rassetScraps.TransDate == dateNull());
            addComponentsToScraps.enabled(rAssetComponents_ds.anyMarked() || rassetComponents.TransDate != dateNull());
            break;
    }
}

public void updateDesign(InventDimFormDesignUpdate _mode, IdentifierName _dsName = '')
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            Set set = new Set(Types::String);
            if (!inventDimFrmComp)
            {
                set.add(inventDimComponents_ds.name());
                inventDimFrmComp = InventDimCtrl_Frm_Scrap_RU::newFromFormValidDataSources(element, set);
            }

            if (!inventDimFrmScrap)
            {
                set.add(inventDimScrap_ds.name());
                inventDimFrmScrap = InventDimCtrl_Frm_Scrap_RU::newFromFormValidDataSources(element, set);
            }
            // No break

        case InventDimFormDesignUpdate::Active:
            switch (_dsName)
            {
                case rAssetComponents_ds.name():
                    inventDimFrmComp.formActiveSetup(InventDimGroupSetup::newItemId(rAssetComponents.ItemId));
                    inventDimFrmComp.formSetControls(true);
                    break;

                case rAssetScraps_ds.name():
                    inventDimFrmScrap.formActiveSetup(InventDimGroupSetup::newItemId(rAssetScraps.ItemId));
                    inventDimFrmScrap.formSetControls(true);
                    break;

                default:
                    inventDimFrmComp.formSetControls(true);
                    inventDimFrmScrap.formSetControls(true);
                    break;
            }
            break;

        case InventDimFormDesignUpdate::FieldChange:
            switch (_dsName)
            {
                case rAssetComponents_ds.name():
                    inventDimFrmComp.formActiveSetup(InventDimGroupSetup::newItemId(rAssetComponents.ItemId));
                    inventDimFrmComp.formSetControls(false);
                    break;

                case rAssetScraps_ds.name():
                    inventDimFrmScrap.formActiveSetup(InventDimGroupSetup::newItemId(rAssetScraps.ItemId));
                    inventDimFrmScrap.formSetControls(false);
                    break;

                default:
                    break;
            }
            break;

        default :
            throw error(strFmt("@SYS54195", funcName()));
    }
}

public void linkActive()
{
    super();

    element.allowCreateRAssetComponents();
}

public void delete()
{
    super();

    element.allowCreateRAssetComponents();
}

public void init()
{
    const str rAssetComponentsTableName = 'RAssetComponents';

    super();

    this.query()
        .dataSourceName(rAssetComponentsTableName)
        .addRange(fieldNum(RAssetScrap, Assembling))
        .value(queryValue(NoYes::Yes));
}

public void write()
{
    rAssetComponents.InventDimId = InventDim::findOrCreate(inventDimComponents).InventDimId;
    InventMovement::setAutoReserving(rAssetComponents);

    super();

    if (rAssetComponents.InventDimId != inventDimComponents.InventDimId)
    {
        inventDimComponents.data(InventDim::find(rAssetComponents.InventDimId));
        inventDimComponents_ds.setCurrent();
    }
}

public int active()
{
    int ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active, this.name());
    inventDimComponents_ds.active();
    tab.tab(1);
    element.setEnableDisable();
    element.allowCreateRAssetComponents();

    return ret;
}

public void displayOption(Common _assetScrap, FormRowDisplayOption _options)
{
    RAssetScrap assetScrap = _assetScrap;

    if (assetScrap.StornoRecId
        && tab.tab() == 1)
    {
        _options.backColor(180 << 16  + 180 << 8 + 180);
    }

    super(_assetScrap, _options);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (rAssetTable.AssetType == RAssetType::LowCostAssets)
    {
        super(_formControl, _filterStr);
    }
    else
    {
        InventTable::lookupItemIdAsset_RU(_formControl, false);
    }
}

public void modified()
{
    super();

    rAssetComponents.initFromInventTable(InventTable::find(rAssetComponents.ItemId));
    inventDimComponents.initFromInventTable(InventTable::find(rAssetComponents.ItemId));
    rAssetComponents.InventDimId = InventDim::findOrCreate(inventDimComponents).InventDimId;
    element.updateDesign(InventDimFormDesignUpdate::FieldChange, rAssetComponents_ds.name());
}

public void displayOption(Common _assetScrap, FormRowDisplayOption _options)
{
    RAssetScrap assetScrap = _assetScrap;

    if (assetScrap.StornoRecId
        && tab.tab() == 1)
    {
        _options.backColor(180 << 16  + 180 << 8 + 180);
    }

    super(_assetScrap, _options);
}

public void init()
{
    const str rAssetScrapsTableName = 'RAssetScraps';

    super();

    this.query()
        .dataSourceName(rAssetScrapsTableName)
        .addRange(fieldNum(RAssetScrap, Assembling))
        .value(queryValue(NoYes::No));
}

public void write()
{
    rassetScraps.InventDimId = InventDim::findOrCreate(inventDimScrap).InventDimId;

    super();

    if (rassetScraps.InventDimId != inventDimScrap.InventDimId)
    {
        inventDimScrap.data(InventDim::find(rassetScraps.InventDimId));
        inventDimScrap.write();
    }
}

public int active()
{
    int ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active, this.name());
    inventDimScrap_ds.active();
    tabScrap.tab(1);
    element.setEnableDisable();

    return ret;
}

public void modified()
{
    super();

    rassetScraps.CostPrice = rassetScraps.calcCostPrice();
    rassetScraps_ds.active();
}

