Method.Source:
[Form]
public class RAssetInventIssueTrans extends FormRun
{
    public RAssetInventIssueTable assetInventIssueTable;
    public InventDimCtrl_Frm_AssetInventIssue_RU inventDimCtrl;

}

public void enableButtons()
{
    InventTable inventTable = InventTable::find(rAssetInventIssueTrans.ItemId);
    rAssetOpenQty.enabled(! inventTable.AssetGroupId_RU);
}

public void enableClearFields()
{
    InventTable inventTable = InventTable::find(rAssetInventIssueTrans.ItemId);
    boolean enable = ! inventTable.AssetGroupId_RU;

    if (inventTable.AssetGroupId_RU)
    {
        rAssetInventIssueTrans.QuotaTypesRecId = 0;
    }

    rAssetInventIssueTrans_ds.object(fieldNum(RAssetInventIssueTrans, QuotaTypesRecId))
        .enabled(enable);

    rAssetInventIssueTrans_ds.object(fieldNum(RAssetInventIssueTrans, WrkCtrId))
        .enabled(enable);
}

public void init()
{
    assetInventIssueTable = element.args().record();

    super();

    element.updateInventDesign(InventDimFormDesignUpdate::Init);

    AssetDeprGrid_AssetAccount.parmPostingType(LedgerPostingType::None);
    LedgerAccountForDepreciationBonus.parmPostingType(LedgerPostingType::None);
}

public void updateDesign()
{
    InventDim inventDimUsed = InventDim::findDim(inventDim);
    boolean enable = rAssetInventIssueTrans.rAssetTableUsed(inventDimUsed.InventDimId).RecId == 0;

    rAssetInventIssueTrans_DS.object(fieldNum(RAssetInventIssueTrans, UsingPeriod)).allowEdit(enable);
    rAssetInventIssueTrans_DS.object(fieldNum(RAssetInventIssueTrans, Qty)).allowEdit(enable);

    rAssetInventIssueTrans_ds.allowEdit(!assetInventIssueTable.Posted);
    rAssetInventIssueTrans_ds.allowCreate(!assetInventIssueTable.Posted);
    rAssetInventIssueTrans_ds.allowDelete(!assetInventIssueTable.Posted);

    inventDim_ds.allowEdit(!assetInventIssueTable.Posted);
    inventDim_ds.allowCreate(!assetInventIssueTable.Posted);
    inventDim_ds.allowDelete(!assetInventIssueTable.Posted);

    this.enableClearFields();
    this.enableButtons();
}

public void updateInventDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimCtrl)
            {
                inventDimCtrl = InventDimCtrl_Frm_AssetInventIssue_RU::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimCtrl.formActiveSetup(InventDimGroupSetup::newItemId(rAssetInventIssueTrans.ItemId));
            inventDimCtrl.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            inventDimCtrl.formActiveSetup(InventDimGroupSetup::newItemId(rAssetInventIssueTrans.ItemId));
            inventDimCtrl.formSetControls(false);
            break;

        default :
            throw error(strFmt("@SYS54195", funcName()));
    }
}

public void initValue()
{
    super();

    rAssetInventIssueTrans.initFromRAssetInventIssueTable(assetInventIssueTable);

    if (!inventDim.InventLocationId
        && !rAssetInventIssueTrans.RecId)
    {
        inventDim.InventLocationId = assetInventIssueTable.InventLocationId;
        inventDim.InventSiteId = InventLocation::find(assetInventIssueTable.InventLocationId).InventSiteId;
    }

    InventTable inventTable = InventTable::find(rAssetInventIssueTrans.ItemId);

    if (!inventDim.InventProfileId_RU
        && !rAssetInventIssueTrans.RecId
        && inventTable.AssetGroupId_RU)
    {
        inventDim.InventProfileId_RU = RAssetParameters::find().LowCostAssetInventProfileId;
    }

    rAssetInventIssueTrans.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
}

public boolean validateWrite()
{
    InventTable inventTable = InventTable::find(rAssetInventIssueTrans.ItemId);

    boolean ret = super();

    rAssetInventIssueTrans.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    if (rAssetInventIssueTrans.orig().InventDimId != rAssetInventIssueTrans.InventDimId)
    {
        rAssetInventIssueTrans.calcDeprAccountForJournal();
    }

    RAssetMainGroupId mainGroupId;
    RAssetInventIssueQuotas assetInventIssueQuotas;
    if (!rAssetInventIssueTrans.rAssetTableUsed())
    {
        if (!inventTable.AssetGroupId_RU)
        {
            assetInventIssueQuotas = RAssetInventIssueQuotas::findQuotas(rAssetInventIssueTrans.EmployeeTable_RU,
                rAssetInventIssueTrans.QuotaTypesRecId,
                InventTable::find(rAssetInventIssueTrans.ItemId).itemGroupId(),
                rAssetInventIssueTrans.ItemId);
        }

        RAssetLifeTime usingPeriod;
        if (rAssetInventIssueTrans.RecId
            || rAssetInventIssueTrans.UsingPeriod > 0)
        {
            usingPeriod = rAssetInventIssueTrans.UsingPeriod;
        }
        else
        {
            if (inventTable.AssetGroupId_RU)
            {
                RAssetMainGroupMember assetMainGroupMember;
                select firstonly AssetGroup from assetMainGroupMember
                    where assetMainGroupMember.AssetMainGroupId == inventTable.AssetGroupId_RU
                        && assetMainGroupMember.AssetStandardId  == RAssetParameters::find().StandardIdDefault;

                usingPeriod = RAssetGroup::find(assetMainGroupMember.AssetGroup).Lifetime;
            }
            else
            {
                usingPeriod = assetInventIssueQuotas.UsagePeriod;
            }
        }

        if (!rAssetInventIssueTrans.CostPrice
            && !rAssetInventIssueTrans.RecId)
        {
            rAssetInventIssueTrans.CostPrice = RAssetDeprAccountForJournalCalc::construct()
                .inventCostAmount(rAssetInventIssueTrans);
        }
        mainGroupId = RAssetGroupDefinitionPeriod::findMainGroupId(rAssetInventIssueTrans.rAssetInventIssueTable().TransDate,
            rAssetInventIssueTrans.ItemId,
            usingPeriod,
            rAssetInventIssueTrans.CostPrice);

        if ((assetInventIssueQuotas && mainGroupId)
            || (inventTable.AssetGroupId_RU && mainGroupId))
        {
            if (assetInventIssueQuotas.UsagePeriod < 0
                && ! inventTable.AssetGroupId_RU)
            {
                ret = checkFailed("@GLS114003");
            }
        }
        else
        {
            if (!assetInventIssueQuotas
                && !inventTable.AssetGroupId_RU)
            {
                ret = checkFailed("@GLS114004");
            }

            if (!mainGroupId)
            {
                ret = checkFailed("@GLS113919");
            }
        }
    }
    else
    {
        mainGroupId = rAssetInventIssueTrans.assetMainGroupId();

        if (!mainGroupId)
        {
            ret = checkFailed("@GLS113919");
        }
    }
    return ret;
}

public int active()
{
    int ret = super();

    element.updateDesign();
    element.updateInventDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void modified()
{
    super();

    element.updateInventDesign(InventDimFormDesignUpdate::FieldChange);
    element.enableClearFields();
    element.enableButtons();

    rAssetInventIssueTrans_ds.refresh();
    rAssetInventIssueTrans_ds.reread();
}

public void modified()
{
    super();

    rAssetInventIssueTrans_ds.refresh();
    rAssetInventIssueTrans_ds.reread();
}

public void modified()
{
    super();

    rAssetInventIssueTrans.Qty = abs(decRound(rAssetInventIssueTrans.Qty, 0));
}

