[Form]
public class RAssetStandards extends FormRun
{
    public RAssetParameters assetParameters;
    public RAssetId assetId;
    public Integer lastSelection;
    public QueryBuildRange criteriaDeferralsAccount;
    public RAssetPurchTrans assetPurchTrans;

}

public void enableButtons()
{
    RAssetTable rAssetTable = RAssetTable::find(rassetStandards.AssetId);
    RAssetStatusType assetStatusType = RAssetStatusType::construct(rAssetTable);
    RAssetParameters rAssetParameters  = RAssetParameters::find();
    RAssetDeprMethod method = rassetStandards.assetDeprMethod(rassetStandards.LastDepDate);
    boolean hasNotHistory = (select firstonly RecId from rAssetLifeHist
        where rAssetLifeHist.AccountNum == rassetStandards.AssetId
            && rAssetLifeHist.AssetStandardId == rassetStandards.AssetStandardId
            && rAssetLifeHist.ChangeDate).RecId == 0;

    rassetStandards_ds.allowEdit(true);
    tabPageDeferrals.visible(rassetStandards.AssetStandardId == rAssetParameters.StandardIdDefault
        || rassetStandards.AssetStandardId == rAssetParameters.StandardTaxIdDefault);

    if (assetStatusType.disposed())
    {
        rassetStandards_ds.allowCreate(false);
        rassetStandards_ds.allowDelete(false);
        rassetStandards_ds.allowEdit(false);
        rassetDeferralsAccounts_ds.allowCreate(false);
        rassetDeferralsAccounts_ds.allowEdit(false);
        rassetDeferralsAccounts_ds.allowDelete(false);
    }

    rassetStandards_ds.object(fieldNum(RAssetStandards, Say))
        .visible(RAssetParameters::find().StandardIdDefault == rassetStandards.AssetStandardId);

    rassetStandards_ds.object(fieldNum(RAssetStandards, SubGroupId))
        .visible(method == RAssetDeprMethod::TaxNonLinearGroup);

    rassetStandards_ds.object(fieldNum(RAssetStandards, SubGroupId))
        .allowEdit(! rassetStandards.Say && !rassetStandards.LastDepDate && hasNotHistory);

    costLimitGroup.visible(rassetStandards.isNonLinearDeprMethod(rassetStandards.LastDepDate));

    rassetStandards_ds.object(fieldNum(RAssetStandards, Say))
        .allowEdit(!rassetStandards.rAssetDeprProfile && !rassetStandards.LastDepDate);

    assetLifeHistory.enabled(!rassetStandards.Say);

    rassetStandards_ds.object(fieldNum(RAssetStandards, rAssetDeprProfile))
        .allowEdit(! rassetStandards.Say && !rassetStandards.LastDepDate && hasNotHistory);

    RAssetSumCalc rassetSumCalcTrans = RAssetSumCalc_Trans::newAssetPeriod(rassetStandards.AssetId, rassetStandards.AssetStandardId);
    acquisitionPrice.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    rAssetStandards_AcquisitionPrice.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    gridAssetGroup.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    assetGroup.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    rAssetStandards_assetStandardId.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    assetStandardId.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    currencyCode.allowEdit(!rassetSumCalcTrans.acquisitionValue());
    rAssetStandards_DepStartDate.allowEdit(!rassetStandards.LastDepDate);
    depStartDate.allowEdit(!rassetStandards.LastDepDate);

    rassetStandards_ds.object(fieldNum(RAssetStandards, BlockDepreciation))
        .visible(rassetStandards.AssetStandardId == rAssetParameters.StandardIdDefault);
}

public void init()
{
    RAssetITable rAssetITable = element.args().caller() as RAssetITable;
    if (rAssetITable)
    {
        assetPurchTrans = rAssetITable.rAssetPurchTrans();
    }

    super();

    assetParameters = RAssetParameters::find();
    navigationList.visible(true);
}

public void updateAcquisitionPrice()
{
    if (assetPurchTrans.TableTableId)
    {
        switch(assetPurchTrans.TableTableId)
        {
            case tableNum(PurchLine):
                if (assetPurchTrans.purchLine())
                {
                    rAssetStandards.initFromPurchLine(assetPurchTrans.purchLine());
                }
                break;

            case tableNum(LedgerJournalTrans):
                if (assetPurchTrans.ledgerJournalTrans())
                {
                    rAssetStandards.initFromLedgerJournalTrans(assetPurchTrans.ledgerJournalTrans());
                }
                break;

            default:
                break;
        }
    }
}

public void initValue()
{
    super();

    RAssetTable assetTable = RAssetTable::find(rAssetStandards.AssetId);
    rAssetStandards.DepStartDate = RAssetGroup::initDepStartDate(rAssetStandards.AssetGroup, assetTable.AcquisitionDate, assetTable.AccountNum);

    if (assetPurchTrans.TableTableId
        && !RAssetStandards::find(rAssetStandards.AssetId,assetParameters.StandardIdDefault))
    {
        switch(assetPurchTrans.TableTableId)
        {
            case tableNum(PurchLine):
                if (assetPurchTrans.purchLine())
                {
                    rAssetStandards.AssetStandardId = assetParameters.StandardIdDefault;
                    rAssetStandards.updateFromStandardTable(assetParameters.StandardIdDefault);
                    rAssetStandards.initFromPurchLine(assetPurchTrans.purchLine());
                    rAssetStandards.updateFromAssetGroup(RAssetGroup::find(rAssetStandards.AssetGroup));
                }
                break;

            case tableNum(LedgerJournalTrans):
                if (assetPurchTrans.ledgerJournalTrans())
                {
                    rAssetStandards.AssetStandardId = assetParameters.StandardIdDefault;
                    rAssetStandards.updateFromStandardTable(assetParameters.StandardIdDefault);
                    rAssetStandards.initFromLedgerJournalTrans(assetPurchTrans.ledgerJournalTrans());
                }
                break;

            default:
                break;
        }
    }
}

public int active()
{
    int ret = super();

    element.enableButtons();

    return ret;
}

public void write()
{
    super();

    if (tabPageDeferrals.visible())
    {
        RAssetDeferralsAccounts::initFromRassetDeferralsAccount(rassetStandards.AssetStandardId,rassetStandards.AssetGroup,rassetStandards.AssetId);
        rAssetDeferralsAccounts_ds.research();
    }
}

public void jumpRef()
{
    Args args = new Args();
    args.record(rAssetStandards);
    new MenuFunction(menuitemDisplayStr(RAssetGroup), MenuItemType::Display).run(args);
}

public void modified()
{
    super();

    element.updateAcquisitionPrice();
    rassetStandards_ds.refresh();
    rassetStandards_ds.rereadReferenceDataSources();
    element.enableButtons();
}

public void modified()
{
    super();

    element.updateAcquisitionPrice();
    FormDataUtil::getFormDataSource(rassetStandards).refresh();
    element.enableButtons();
}

public void modified()
{
    super();

    element.updateAcquisitionPrice();
    rassetStandards_ds.refresh();
}

