public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(RAssetSubGroup), _formControl);
    Query query = new Query();

    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, SubGroupId), true);
    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, Name));
    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, Factor));

    query.addDataSource(tableNum(RAssetSubGroup))
        .addRange(fieldNum(RAssetSubGroup, GroupId))
        .value(queryValue(rassetStandards.AssetGroup));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void init()
{
    super();

    criteriaDeferralsAccount = this.query()
        .dataSourceTable(tableNum(RAssetDeferralsAccounts))
        .addRange(fieldNum(RAssetDeferralsAccounts, RassetDeferralsType));

}

public void executeQuery()
{
    criteriaDeferralsAccount.value(queryValue(deferralsTypeCombo.selection()));

    super();
}

public void lookup()
{
    RAssetGroup::lookupGroup(this, rassetStandards.AssetStandardId);
}

public void tabChanged(int _fromTab, int _toTab)
{
    const int tabPageDeferralsNumber = 5;

    if (_toTab == tabPageDeferralsNumber
        && !rAssetStandards
        && !rAssetStandards.validateWrite())
    {
        DetailTab.tab(_fromTab);
    }
    else
    {
        super(_fromTab, _toTab);
    }
}

public void lookup()
{
    RAssetGroup::lookupGroup(this, rassetStandards.AssetStandardId);
}

public void enter()
{
    super();

    lastSelection = this.selection();
}

public int selectionChange()
{
    int ret;

    if (!rassetDeferralsAccounts_ds.leaveRecord())
    {
        this.selection(lastSelection);
    }
    else
    {
        ret = super();

        rassetDeferralsAccounts_ds.executeQuery();
    }

    return ret;
}

public void lookup()
{
    RAssetGroup::lookupGroup(this, rassetStandards.AssetStandardId);
}
