public class RAssetTable extends FormRun implements RAssetITable, RAssetITaxAllowanceHistoryRefreshable
{
    public RAssetPurchTrans rAssetPurchTrans;
    public RAssetTableForm assetTableForm;

}

public void init()
{
    super();

    this.initFromJumpRef();

    RAssetGroupTypes assetGroupType;
    if (this.args().parmEnumType() == enumNum(RAssetGroupTypes))
    {
        assetGroupType = this.args().parmEnum();
    }
    else
    {
        RAssetTable assetTable;
        if (element.args().dataset() == tableNum(RAssetTable))
        {
            assetTable = element.args().record()
                ? element.args().record()
                : element.args().lookupRecord();
        }
        else
        {
            assetTable = RAssetTable::find(SysTableLookup::getCallerControl(element.args()).valueStr());
        }

        assetGroupType  = RAssetMainGroup::find(assetTable.AssetGroup).GroupType;
    }

    assetTableForm = RAssetTableForm::construct(assetGroupType);
    assetTableForm.parmTaxAccountsTab(taxAccountsTab);
    assetTableForm.parmVehicleProperties(vehicleProperties);
    assetTableForm.parmStructureTabPage(structureTabPage);
    assetTableForm.parmDataSourceAssetTable(rAssetTable_ds);
    assetTableForm.parmButtonRAssetReportOC1_DeedOfConv(rAssetReportOC1_DeedOfConveyance);
    assetTableForm.parmButtonRAssetReportOC1_Trans(rAssetReportOC1_Trans);
    assetTableForm.parmButtonRAssetReportOC1_MoveHist(rAssetReportOC1_MoveHist);
    assetTableForm.parmButtonRAssetReportOC1a_Trans(rAssetReportOC1a_Trans);
    assetTableForm.parmButtonRAssetReportOC1a_DeedOfConv(rAssetReportOC1a_DeedOfConveyance);
    assetTableForm.parmButtonRAssetReportOC1a_MoveHist(rAssetReportOC1a_MoveHist);
    assetTableForm.parmButtonRAssetReportOC2(rAssetReportOC2);
    assetTableForm.parmButtonRAssetReportOC3(rAssetReportOC3);
    assetTableForm.parmButtonRAssetReportOC4(rAssetReportOC4);
    assetTableForm.parmButtonRAssetReportOC4a(rAssetReportOC4a);
    assetTableForm.parmButtonFormOS6(formOS6);
    assetTableForm.parmButtonMenuAct(menuAct);
    assetTableForm.parmButtonRAssetReportMB4(rAssetReportMB4);
    assetTableForm.parmButtonRAssetReportMB8(rAssetReportMB8);
    assetTableForm.parmButtonAssetLending(lease);
    assetTableForm.parmButtonHistCompanyIssue(histCompanyIssue);
    assetTableForm.parmButtonHistCompanyReceipt(histCompanyReceipt);
    assetTableForm.init();

    rAssetTable_ds.query(assetTableForm.queryRAssetTable(rAssetTable_ds.query()));

    this.design()
        .caption(assetTableForm.formCaption());
}

public void allowEditForm(boolean _edit)
{
    DictTable dictTable = new DictTable(rAssetTable_ds.table());

    for (int i = 1; i <= dictTable.fieldCnt(); i++)
    {
        for (int j = 1; j <= dictTable.fieldObject(dictTable.fieldCnt2Id(i)).arraySize(); j++)
        {
            FormDataObject dataObject = rAssetTable_ds.object(fieldId2Ext(dictTable.fieldCnt2Id(i), j));
            if (dataObject)
            {
                dataObject.allowEdit(_edit);
            }
        }
    }
}

public void buildComposedOf(RAssetId _accountNum, int _treeHeader, NoYes _list = NoYes::No)
{
    const int FTI272 = 272;

    RAssetTable assetTableLocal;
    while select AccountNum from assetTableLocal
        where assetTableLocal.AccountNumMain == _accountNum
    {
        FormTreeItem formTreeItem = new FormTreeItem(assetTableLocal.AccountNum, FTI272, -1, assetTableLocal.AccountNum);
        int node = structure.addItem(_treeHeader, FormTreeAdd::Sort, formTreeItem);
        this.buildComposedOf(assetTableLocal.AccountNum,node, _list || _accountNum == assetTableLocal.AccountNum);

        if (assetTableLocal.AccountNum == rassetTable.AccountNum)
        {
            structure.select(node);
        }
    }

    structure.expand(_treeHeader, FormTreeExpand::Expand);
}

public boolean canClose()
{
    boolean ret = true;

    if (rAssetPurchTrans.TableTableId
        && !rAssetPurchTrans.RecId
        && rAssetTable.AccountNum)
    {
        if (rAssetTable_Ds.validateWrite())
        {
            rAssetTable_Ds.write();
            if (RAssetStandards::find(rAssetTable.AccountNum, RAssetParameters::standardIdDefault())
                && !rAssetPurchTrans.Posted)
            {
                rAssetPurchTrans.AssetId = rassetTable.AccountNum;
                if (rAssetPurchTrans.validateField(fieldNum(RAssetPurchTrans, AssetId))
                    && rAssetPurchTrans.validateWrite())
                {
                    FormDataUtil::getFormDataSource(rAssetPurchTrans).refresh();
                }
                else
                {
                    rAssetPurchTrans.AssetId = rAssetPurchTrans.orig().AssetId;
                }
            }
        }
        else
        {
            ret = false;
        }
    }

    return ret;
}

public void createStruct()
{
    if (rAssetTable.AccountNum)
    {
        RAssetId assetIdTop = rassetTable.topMainAsset();

        FormTreeItem formTreeStructure = new FormTreeItem(assetIdTop, 272, -1, assetIdTop);
        structure.delete(structure.getRoot());

        this.buildComposedOf(assetIdTop, structure.addItem(0, FormTreeAdd::Sort, formTreeStructure));
    }
}

public void enableButtons()
{
    RAssetStatusType assetStatusType = RAssetStatusType::construct(rassetTable);
    Object assetBelongedObject = rassetTable_ds.object(fieldNum(RAssetTable, AssetBelonged));
    boolean noAcquisition = rassetTable.Status == RAssetStatus::NoAcquisition
        || rassetTable.Status == RAssetStatus::PurchInvoice;

    boolean noDistribution = !RAssetDistribution::distributionsExistForAsset(rAssetTable.AccountNum);
    boolean nonZeroTaxBaseExistsInHistory = RAssetTaxAllowanceHistory::nonZeroTaxBaseExists(rAssetTable.AccountNum);
    boolean nonEmptyCadastrialNumberExistsInHistory = RAssetTaxAllowanceHistory::nonEmptyLandCadastrialNumberExists(rAssetTable.AccountNum);
    boolean nonEmptyRoomCadastrialNumberExistsInHistory = RAssetTaxAllowanceHistory::nonEmptyRoomCadastrialNumberExists(rAssetTable.AccountNum);
    boolean nonEmptyLandTypeExistsInHistory = RAssetTaxAllowanceHistory::nonEmptyLandTypeExists(rAssetTable.AccountNum);
    boolean nonEmptyOwnedShareExistsInHistory = RAssetTaxAllowanceHistory::nonEmptyOwnedShareExists(rAssetTable.AccountNum);

    element.createStruct();

    if (assetStatusType.disposed())
    {
        rAssetTable_ds.allowEdit(false);
    }
    else
    {
        rAssetTable_ds.allowEdit(true);
        barcode.allowEdit(!RAssetParameters::find().BarcodeEqualsAssetNumber);
        assetNum.allowEdit(rassetTable.allowEditOfAssetNum());
        assetTable_AcquisitionDate.allowEdit(noAcquisition);
        assetTable_AssetUse.allowEdit(noAcquisition);
        vendAccount.allowEdit(!rassetTable.PurchId && !rassetTable.VendInvoiceId);
        custAccount.allowEdit(!rassetTable.SalesId && !rassetTable.CustInvoiceId);

        taxCode.allowEdit((rassetTable.AssessedTaxType != RAssetAssessedTaxType::Section3
                || rAssetTable.AssetType == RAssetType::LandBuilding
                || CompanyInfo::find().rAlienCorpName)
            && rassetTable.AssessedTaxType != RAssetAssessedTaxType::Second
            && noDistribution);
        landTaxCode.allowEdit(noDistribution);

        if (assessedTax.visible() || landTaxGroup.visible())
        {
            boolean enableShare = (noDistribution || assessedTax.visible()) && !nonEmptyOwnedShareExistsInHistory;
            RAssetTable_DS.object(fieldNum(RAssetTable, DenominatorShare)).allowEdit(enableShare);
            RAssetTable_DS.object(fieldNum(RAssetTable, NumeratorShare)).allowEdit(enableShare);
        }

        landTax_TaxBase.allowEdit(noDistribution && !nonZeroTaxBaseExistsInHistory);
        landProperties_LandType.allowEdit(!nonEmptyLandTypeExistsInHistory);
        landProperties_LandCadastralNum.allowEdit(!nonEmptyCadastrialNumberExistsInHistory);

        CadastralDataEdit_landCadastralNumEdit.allowEdit(!nonEmptyCadastrialNumberExistsInHistory);
        CadastralDataEdit_roomCadastralNumEdit.allowEdit(!nonEmptyRoomCadastrialNumberExistsInHistory);
        CadastralDataEdit_taxBaseEdit.allowEdit(!nonZeroTaxBaseExistsInHistory);

        RAssetTable_DS.object(fieldNum(RAssetTable, TransportTaxDeductionValue)).allowEdit(rAssetTable.TaxDeductionRecId != 0);
    }

    formOS6.enabled(!noAcquisition);
}

public void initFromJumpRef()
{
    if (element.args()
        && element.args().caller())
    {
        FormRun formRun = element.args().caller() as FormRun;

        if (formRun
            && formRun.dataSource()
            && formRun.dataSource().cursor().TableId == tableNum(RAssetPurchTrans))
        {
            rAssetPurchTrans = formRun.dataSource().cursor();

            switch (rAssetPurchTrans.TableTableId)
            {
                case tableNum(PurchLine):
                    rAssetTable_ds.query()
                        .dataSourceTable(tableNum(RAssetTable))
                        .addRange(fieldNum(RAssetTable, AssetGroup))
                        .value(InventTable::find(rAssetPurchTrans.purchLine().ItemId).AssetGroupId_RU);
                    break;

                case tableNum(VendInvoiceInfoLine):
                    rAssetTable_ds.query()
                        .dataSourceTable(tableNum(RAssetTable))
                        .addRange(fieldNum(RAssetTable, AssetGroup))
                        .value(InventTable::find(rAssetPurchTrans.vendInvoiceInfoLine().ItemId).AssetGroupId_RU);
                    break;

                default:
                    break;
            }
        }
    }
}

public RAssetPurchTrans rAssetPurchTrans()
{
    return rAssetPurchTrans;
}

public void refreshDS()
{
    rAssetTable_DS.reread();
    rAssetTable_DS.rereadReferenceDataSources();
    rAssetTable_DS.refresh();
}

public void visibleAssetTypeFields()
{
    vehicleProperties.visible(rassetTable.AssetType == RAssetType::Vehicle);
    landProperties.visible(rassetTable.AssetType == RAssetType::Land);
    assessedTax.visible(rassetTable.AssetType == RAssetType::LandBuilding);
    rAssetTable_DS.object(fieldNum(RAssetTable, AssessedTaxNonTaxable)).visible(rassetTable.isMovablesFixedAssetType());
    transportTax.visible(rassetTable.AssetType == RAssetType::Vehicle);
    landTaxGroup.visible(rassetTable.AssetType == RAssetType::Land);
    govRegistration.visible(rassetTable.isRealtyOrVehicle());
    distribution.enabled(rassetTable.AssetType != RAssetType::Vehicle);
    taxAllowanceHistory.enabled(rassetTable.AssetType == RAssetType::Land
        || rassetTable.AssetType == RAssetType::Vehicle 
        || (rassetTable.AssetType == RAssetType::LandBuilding && rassetTable.assessedTaxBaseType() == RAssetAssessedTaxBaseType::CadastralValue));

    cadastralDataEdit.visible(rAssetTable.AssetType == RAssetType::LandBuilding);
    assessedTaxBaseCalc.visible(rAssetTable.AssetType == RAssetType::LandBuilding);
}

public display Description taxAllowanceType(RAssetTable _rAssetTable)
{
    Description ret;

    if (_rAssetTable.TaxAllowanceRecId)
    {
        ret = enum2str(RAssetTaxAllowance::find(_rAssetTable.TaxAllowanceRecId).TaxAllowanceType);
    }

    return ret;
}

public void write()
{
    super();

    rassetTable.createAssetStandards(rAssetPurchTrans);
}

public void create(boolean _append = false)
{
    super(_append);
    element.visibleAssetTypeFields();
    element.enableButtons();
}

public void displayOption(Common _assetTable, FormRowDisplayOption _options)
{
    const int Value180 = 180;
    RAssetTable assetTable = _assetTable;
    switch(assetTable.Status)
    {
        case RAssetStatus::Closed:
        case RAssetStatus::Disposed:
        case RAssetStatus::Scrapped:
        case RAssetStatus::Sold:

            _options.backColor(Value180 << 16  + Value180 << 8 + Value180);
            break;

        default:
            break;
    }

    super(_assetTable, _options);
}

public void initValue()
{
    super();

    rAssetTable.Status = RAssetStatus::NoAcquisition;

    if (rAssetPurchTrans.TableTableId)
    {
        switch(rAssetPurchTrans.TableTableId)
        {
            case tableNum(PurchLine):
            case tableNum(VendInvoiceInfoLine):
                SalesPurchLine salesPurchLine = rAssetPurchTrans.TableTableId == tableNum(PurchLine)
                    ? rAssetPurchTrans.purchLine() as SalesPurchLine
                    : rAssetPurchTrans.vendInvoiceInfoLine() as SalesPurchLine;

                if (salesPurchLine)
                {
                    rassetTable.initFromPurchLine(salesPurchLine);
                    rassetTable.initFromInventTable(salesPurchLine.salesPurchLineInterface().inventTable());
                    RAssetMainGroup assetMainGroup = RAssetMainGroup::find(rassetTable.AssetGroup);
                    if (assetMainGroup)
                    {
                        rassetTable.initFromAssetGroup(assetMainGroup);
                    }
                    rassetTable_ds.write();
                }
                break;

            case tableNum(LedgerJournalTrans):
                if (rAssetPurchTrans.ledgerJournalTrans())
                {
                    rassetTable.initFromLedgerJournalTrans(rAssetPurchTrans.ledgerJournalTrans());
                }
                break;

            default:
                break;
        }
    }

}

public int active()
{
    int ret = super();

    assetTableForm.assetTable(rAssetTable);
    assetTableForm.enablePrintButtons();
    element.visibleAssetTypeFields();
    element.enableButtons();

    return ret;
}

