public void init()
{
    super();

    if (element.args()
        && element.args().record()
        && element.args().dataset() == tableNum(RAssetTrans))
    {
        if (element.args().menuItemName() == menuItemDisplayStr(RAssetTransLink))
        {
            element.showTransLink(element.args()
                .record());
        }
        else if (!element.args().caller())
        {
            rAssetTrans_q.dataSourceTable(tablenum(RAssetTrans))
                .clearDynalinks();
        }
    }
    else if (element.args().dataset() == tableNum(LedgerJournalTrans))
    {
        LedgerJournalTrans ledgerJournalTrans = element.args().record();

        rAssetTrans_ds.query()
            .dataSourceTable(tableNum(RAssetTrans))
            .addRange(fieldNum(RAssetTrans, Voucher))
            .value(queryValue(ledgerJournalTrans.Voucher));

        LedgerJournalTrans_RAsset ledgerJournalTrans_RAsset = LedgerJournalTrans_RAsset::find(ledgerJournalTrans.RecId);

        rAssetTrans_ds.query()
            .dataSourceTable(tableNum(RAssetTrans))
            .addRange(fieldNum(RAssetTrans, TransDate))
            .value(queryValue(ledgerJournalTrans_RAsset.getTransDate(ledgerJournalTrans)));
    }
}

public void setButtonAccess()
{
    formOS1.enabled(false);
    formOS1a.enabled(false);
    formOS3.enabled(false);
    formOS4.enabled(false);
    formOS4a.enabled(false);
    formOS1_DeedOfConveyance.enabled(false);
    formOS1a_DeedOfConveyance.enabled(false);

    boolean isFixedAsset = RAssetTable::find(rassetTrans.AccountNum).isFixedAssetType();
    boolean isStorno = rassetTrans.StornoRecId != 0;

    if (isFixedAsset)
    {
        switch (rassetTrans.AssetTransType)
        {
            case RAssetTransType::DisposalScrap:
            case RAssetTransType::Disposal:
                formOS4.enabled(!isStorno);
                formOS4a.enabled(!isStorno);
                break;

            case RAssetTransType::DisposalSale:
                formOS4.enabled(!isStorno);
                formOS4a.enabled(!isStorno);
                formOS1_DeedOfConveyance.enabled(true);
                formOS1a_DeedOfConveyance.enabled(true);
                break;

            case RAssetTransType::Revaluation:
            case RAssetTransType::AcquisitionAdj:
                formOS3.enabled(rassetTrans.PostingType != RAssetPostingType::PartialTakeDown && !isStorno);
                // NO BREAK !!!

            case RAssetTransType::DepreciationAdj:
                formOS4.enabled(rassetTrans.PostingType == RAssetPostingType::PartialTakeDown && !isStorno);
                formOS4a.enabled(rassetTrans.PostingType == RAssetPostingType::PartialTakeDown && !isStorno);
                break;

            case RAssetTransType::Acquisition:
                if (rassetTrans.PostingType == RAssetPostingType::CompanyIssue)
                {
                    formOS1_DeedOfConveyance.enabled(true);
                    formOS1a_DeedOfConveyance.enabled(true);
                }
                else
                {
                    formOS1.enabled(true);
                    formOS1a.enabled(true);
                }
                break;

            default:
                break;
        }
    }
}

public void updateDesign()
{
    depreciationBonus.visible(rassetTrans.AssetTransType == RAssetTransType::Acquisition
        || rassetTrans.AssetTransType == RAssetTransType::Revaluation
        || rassetTrans.AssetTransType == RAssetTransType::Depreciation);

    depreciationBonus.allowEdit(((rassetTrans.AssetTransType == RAssetTransType::Acquisition
            || rassetTrans.AssetTransType == RAssetTransType::Revaluation)
        && nextMth(rassetTrans.TransDate) > rassetTrans.assetStandards().LastDepDate)
        && !rassetTrans.LinkedTransRecId
        && !rassetTrans.StornoRecId);


    deprBonusId.visible(rassetTrans.AssetTransType == RAssetTransType::Acquisition
        || rassetTrans.AssetTransType == RAssetTransType::Revaluation);

    bonusPosted.visible(rassetTrans.AssetTransType == RAssetTransType::Acquisition
        || rassetTrans.AssetTransType == RAssetTransType::Revaluation);

    accounts.visible(rassetTrans.AssetTransType == RAssetTransType::Depreciation);

    transactionReversal.enabled(!rassetTrans.StornoRecId
        && rassetTrans.AssetTransType != RAssetTransType::Depreciation
        && rassetTrans.AssetTransType != RAssetTransType::CompanyIssue
        && rassetTrans.AssetTransType != RAssetTransType::CompanyReceipt
        && !RAssetTable::find(rassetTrans.AccountNum).CustInvoiceId); // to have the credit-note custInvoiceTrans for profit register

    RAssetTransLink.enabled(TransLink.RecId != 0);
    CorrectionGroup.visible(TransLink.RecId != 0);

    rAssetTrans_ds.refresh();
}

private void showTransLink(RAssetTrans _source)
{
    RAssetTrans_Q.dataSourceTable(tablenum(RAssetTrans))
        .clearDynalinks();

    QueryBuildDataSource qbds = RAssetTrans_Q.dataSourceTable(tablenum(TransLink_RU));
    qbds.joinMode(JoinMode::InnerJoin);

    findOrCreateRange_W(qbds, fieldnum(TransLink_RU, ReversalGroup), queryValue(TransLink_RU::findSec(_source.TableId, _source.RecId).ReversalGroup));

    CorrectionType.visible(true);

    RAssetTransLink.visible(false);
    TransactionReversal.visible(false);
}

public int active()
{
    int ret = super();

    element.updateDesign();

    return ret;
}

public display AmountMST amount(RAssetTrans _assetTransds)
{
    if (_assetTransds.AssetStandardId == RAssetParameters::find().StandardIdDefault)
    {
        return _assetTransds.AmountMST;
    }
    else
    {
        return _assetTransds.AmountCur;
    }
}

public display CurrencyCode currency(RAssetTrans _assetTransds)
{
    if (_assetTransds.AssetStandardId == RAssetParameters::find().StandardIdDefault)
    {
        return Ledger::accountingCurrency(CompanyInfo::current());
    }
    else
    {
        return _assetTransds.CurrencyCode;
    }
}

public void displayOption(Common _assetTrans, FormRowDisplayOption _options)
{
    RAssetTrans assetTrans = _assetTrans;

    if (assetTrans.StornoRecId && tab.tab() == 1)
    {
        _options.backColor(180 << 16  + 180 << 8 + 180);
    }

    super(_assetTrans, _options);
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query()
        .dataSourceTable(tablenum(TransLink_RU));

    qbds.addSelectionField(fieldnum(TransLink_RU, RecId));
    qbds.addSelectionField(fieldnum(TransLink_RU, TransLinkType));
    qbds.addSelectionField(fieldnum(TransLink_RU, AccountingDate));
}

public void clicked()
{
    RAssetTrans rassetTransCurrent = rassetTrans.data();

    super();

    rassetTrans_ds.executeQuery();
    rassetTrans_ds.positionToRecord(rassetTransCurrent);
    rassetTrans_ds.active();
}

public void tabChanged(int _fromTab, int _toTab)
{
    if(_fromTab == 1 || _toTab == 1)
    {
        rAssetTrans_ds.clearDisplayOption(rAssetTrans_ds.cursor());
        rAssetTrans_ds.refresh();
    }

    super(_fromTab, _toTab);
}
