[Form]
public class RCashTrans extends FormRun
{
    private const int ConPos1 = 1;
    private const int ConPos2 = 2;

    FormRun ledgerTransVoucher;
    LedgerJournalTrans_RCash ledgerJournalTrans_RCash;
    LedgerJournalTrans ledgerJournalTrans;
    boolean calledFromLedgerJournalTrans;

}

public void init()
{
    super();

    calledFromLedgerJournalTrans = this.args() && this.args().dataset() == tableNum(LedgerJournalTrans);
    if (calledFromLedgerJournalTrans)
    {
        ledgerJournalTrans = this.args().record();
        ledgerJournalTrans_RCash = LedgerJournalTrans_RCash::find(ledgerJournalTrans.RecId);

        QueryBuildDataSource ds = rCashTrans_q.dataSourceTable(tableNum(RCashTrans));

        ds.addDynalink(
            fieldNum(RCashTrans, DocType),
            ledgerJournalTrans_RCash,
            fieldNum(LedgerJournalTrans_RCash, DocType));

        ds.addDynalink(
            fieldNum(RCashTrans, DocId),
            ledgerJournalTrans_RCash,
            fieldNum(LedgerJournalTrans_RCash, CashDocId));
    }

    #ISOCountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        RCashDestination.visible(false);
        rCashDestinationTrans_ds.allowEdit(false);
        rCashDestinationTrans_ds.object(fieldNum(RCashDestinationTrans, RCashDestinationTable)).enabled(false);
        rCashDestinationTrans_ds.object(fieldNum(RCashDestinationTrans, RCashDestinationTable)).visible(false);
        rCashDestinationTrans_ds.object(fieldNum(RCashDestinationTrans, AmountCur)).visible(false);
        rCashDestinationTrans_ds.object(fieldNum(RCashDestinationTrans, TransTxt)).visible(false);
    }
}

void ledgerTransVoucherInit()
{
    container values = rCashTrans.voucherTransDateValue();

    Args args = new Args(formStr(LedgerTransVoucher));
    args.caller(this);
    args.record(rCashTrans);

    ledgerTransVoucher = classfactory.formRunClass(args);
    ledgerTransVoucher.init();

    FormDataSource formDataSource = ledgerTransVoucher.dataSource();

    QueryBuildDataSource queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));
    if (queryBuildDataSource == null)
    {
        // Add the SubledgerVoucherGeneralJournalEntry as a datasource since it always contains the voucher
        queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(GeneralJournalEntry));
        queryBuildDataSource = SubledgerVoucherGeneralJournalEntry::addDataSource(queryBuildDataSource);
    }

    QueryBuildRange queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
    queryBuildRange.value(conPeek(values, ConPos1));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate));
    queryBuildRange.value(conPeek(values, ConPos2));

    ledgerTransVoucher.run();
    ledgerTransVoucher.wait();
}

void ledgerTransVoucherLinkActive()
{
    if (ledgerTransVoucher && !ledgerTransVoucher.closed())
    {
        container values = rCashTrans.voucherTransDateValue();

        FormDataSource formDataSource = ledgerTransVoucher.dataSource();
        QueryBuildDataSource queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));
        queryBuildDataSource.clearRanges();

        QueryBuildRange queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
        queryBuildRange.value(conPeek(values, ConPos1));

        queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate));
        queryBuildRange.value(conPeek(values, ConPos2));

        formDataSource.executeQuery();
    }
}

void setAccessButton()
{
    rCashDoc.enabled(
        rCashTrans.DocType != RCashDocType::ExchRateDifference
        && rCashTrans.DocType != RCashDocType::ReportingExchRateDifference
        && rCashTrans.DocType != RCashDocType::Correction);

    exchangeAdjustment_PL.enabled(rCashTrans.DocType != RCashDocType::ExchRateDifference);
    Include.visible(rCashTrans.ExcludeExchAdj_PL);
    Exclude.visible(!rCashTrans.ExcludeExchAdj_PL);
}

void setExchAdjustment_PL(PlExcludeExchAdj _excludeExchAdjustment_PL)
{
    if (DialogButton::Ok == Box::okCancel(strFmt("@GLS113937", fieldPName(RCashTrans, ExcludeExchAdj_PL)), DialogButton::Ok))
    {
        RCashExchAdjPL::setExclExchAdj_PL(rCashTrans, _excludeExchAdjustment_PL);
        rCashTrans_DS.research();
    }
}

public void init()
{
    super();

    // Dynalink is not working properly due to relation on RefRecId field
    if (element.args()
        && element.args().record()
        && element.args().dataset() == tableNum(RCashTrans))
    {
        QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(RCashTrans));
        qbds.clearDynalinks();
        qbds.addDynalink(fieldNum(RCashTrans, RecId), element.args().record(), fieldNum(RCashTrans, RecId));
    }
}

public int active()
{
    int ret = super();

    element.setAccessButton();

    element.ledgerTransVoucherLinkActive();

    if (RCashTransFormDimensionFlight::instance())
    {
        delete_from tmpLedgerBase;
    }

    tmpLedgerBase.DefaultDimension = LedgerDimensionFacade::getDefaultDimensionFromLedgerDimension(rCashTrans.LedgerDimension);
    tmpLedgerBase.RecordTableId = rCashTrans.TableId;
    tmpLedgerBase.RecordRecId = rCashTrans.RecId;

    element.initDimensionEntryControl(RCashTrans.DataAreaId, RCashTrans.TransDate);
    DimensionEntryControl.reactivate();

    tmpLedgerBase.insert();
    TmpLedgerBase_ds.research();

    return ret;
}

public void displayOption(Common _cashTrans, FormRowDisplayOption _options)
{
    const RGBint GrayColor = 192;

    if (_cashTrans.(fieldNum(RCashTrans, TransStatus)) == RCashTransStatus::Canceled)
    {
        _options.backColor(WinAPI::RGB2int(GrayColor, GrayColor, GrayColor));
    }

    super(_cashTrans, _options);
}

public void executeQuery()
{
    OfficialsTrans_RU::searchAccountIdx(
        officialsTrans_RU,
        OffReportType_RU::CashSlip,
        rCashTrans.OffsessionId);
    super();
}
