[Form]
public class RHRMOrderTable extends FormRun
{
    RHRMJournalFormTable              journalFormTable;
    LogisticsPostalAddressFormHandler addressController;
}

void enableFieldsActive()
{
    switch (journalFormTable.journalTypeId())
    {
        case RHRMJournalType::Receive:
            rhrmReportT1a.enabled(true);
            tabLocation.visible(false);
            element.design().caption("@RUP6311");
            break;

        case RHRMJournalType::Transfer:
            rhrmReportT5a.enabled(true);
            tabLocation.visible(false);
            element.design().caption("@GLS106506");
            staffWageChangeTransferGroup.visible(true);
            break;

        case RHRMJournalType::Dismiss:
            docArg.visible(true);
            attachDoc.visible(true);
            tabLocation.visible(false);
            rHRMReportT8a.enabled(true);
            element.design().caption("@GLS106505");
            break;

        case RHRMJournalType::Bonus:
            bonusCode.visible(true);
            bonusType.visible(true);
            rHRMReportT11a.enabled(true);
            tabLocation.visible(false);
            element.design().caption("@GLS106517");
            break;

        case RHRMJournalType::Trip:
            rHRMReportT9a.enabled(true);
            tripOrganization.visible(true);
            element.design().caption("@GLS106486");
            break;

        case RHRMJournalType::Vacation:
            rHRMReportT6a.enabled(true);
            tabLocation.visible(false);
            element.design().caption("@GLS106514");
            reason.visible(false);
            break;

        case RHRMJournalType::StaffList:
            RHRMReport_StaffListOrder.enabled(true);
            tabLocation.visible(false);
            element.design().caption("@RUP2436");
            break;

        case RHRMJournalType::VacationChildCancel:
            tabLocation.visible(false);
            element.design().caption("@RUP3177");
            break;
    }
}

public void init()
{
    journalFormTable = new RHRMJournalFormTable(element);

    super();

    journalFormTable.parmCtrlAllOpenPosted(allOpenPosted);
    journalFormTable.parmCtrlJournalTypeGrid(journalTypeTbl);
    journalFormTable.parmCtrlJournalIdTab(journalIdTab);
    journalFormTable.parmCtrlJournalIdGrid(journalIdTbl);
    journalFormTable.parmCtrlOverview(gridOverview);
    journalFormTable.parmCtrlMarkUserBlock(markUserBlock);
    journalFormTable.parmCtrlMarkInUseBlock(markInUseBlock);
    journalFormTable.parmCtrlSystemBlocked(systemBlocked);
    journalFormTable.parmCtrlLines(lines);
    journalFormTable.parmCtrlCheckJournal(checkJournal);
    journalFormTable.parmCtrlPostJournal(postJournal);
    journalFormTable.parmCtrlUnPostJournal(unPostJournal);
    journalFormTable.parmCtrlCreateNew(createNew);
    journalFormTable.parmCtrlInfolog(showInfolog);
    journalFormTable.parmCtrlBlockImage(blockImage);
    journalFormTable.parmCtrlInitTotals(initTotals);
    journalFormTable.parmCtrllAddessPane(ActionPaneAddress);

    journalFormTable.formInitPost();

    addressController = LogisticsPostalAddressFormHandler::newParameters(rhrmOrderTable_ds, logisticsPostalAddress_ds);
    addressController.callerUpdateQuery(fieldNum(RHRMOrderTable, TripLocation));
}

public boolean leave()
{
    if ( !journalFormTable.datasourceLeavePre())
    {
        return false;
    }

    return super();
}

public int active()
{
    boolean ret;

    addressController.callerActive();
    addressController.callerUpdateButtons(newAddress, editAddress, clearAddress, mapButton);

    journalFormTable.datasourceActivePre();

    ret = super();

    rhrmOrderTable_ds.allowEdit(! rhrmOrderTable.Posted);
    rhrmOrderTable_ds.allowDelete(! rhrmOrderTable.Posted);

    return ret;
}

void create(boolean _append = false)
{
    if (! journalFormTable.datasourceCreatePre())
        return;

    super(_append);

    journalFormTable.datasourceCreatePost();
}

public void executeQuery()
{
    journalFormTable.datasourceExecuteQueryPre();

    super();

    journalFormTable.datasourceExecuteQueryPost();

    rhrmOrderTable_ds.allowCreate(allOpenPosted.selection() == AllOpenPosted::Posted ||
                                  element.args().caller() ? false : true);
}

boolean validateWrite()
{
    boolean ret;

    ret = super();

    ret = journalFormTable.datasourceValidateWritePost(ret);

    return ret;
}

edit JournalMarkInUseBlock inUseBlock(boolean set, JournalTableMap _journalTable, JournalMarkInUseBlock _value)
{
    return journalFormTable.datasourceMethodInUseBlock(set,_journalTable,_value);
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    ret =  journalFormTable.datasourceValidateDeletePost(ret) && ret;

    return ret;
}

