{
    boolean ret;

    ret = super();

    ret = journalFormTable.fieldValidateJournalNameIdPost(ret);

    return ret;
}

int selectionChange()
{
    int ret;

    ret = super();

    journalFormTable.designSelectionChangeAllOpenPosted();

    return ret;
}

