    {
        journalFormTrans.formClosePre();
    }

    super();
}

void enableButtonsActive()
{
}

void enableFields()
{
    bonusGroup.visible(false);

    switch (this.orderType())
    {
        case RHRMJournalType::Receive:
            staffList.visible(true);
            hrmOrganizationIdTable.visible(true);
            hrmPositionIdTable.visible(true);
            startDateTable.visible(true);
            endDateTable.visible(true);

            receiveGroup.visible(true);
            element.design().caption("@RUP6313");
            rhrmReportT1.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::Transfer:
            staffList.visible(true);
            hrmOrganizationIdTable.visible(true);
            hrmPositionIdTable.visible(true);
            startDateTable.visible(true);
            endDateTable.visible(true);

            transferGroup.visible(true);
            element.design().caption("@GLS106510");
            rhrmReportT5.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::Dismiss:
            emplStaffList.visible(true);
            emplOrganisationTable.visible(true);
            emplPositionTable.visible(true);
            dismissDateTable.visible(true);

            dismissGroup.visible(true);

            element.design().caption("@GLS106512");
            rHRMReportT8.enabled(true);
            RHRMReport_EmplFundSum.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::Bonus:
            emplStaffList.visible(true);
            emplOrganisationTable.visible(true);
            emplPositionTable.visible(true);
            valueSumTable.visible(true);

            bonusGroup.visible(true);
            element.design().caption("@GLS106518");
            rHRMReportT11.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::Trip:
            emplStaffList.visible(true);
            emplOrganisationTable.visible(true);
            emplPositionTable.visible(true);
            businessTripTypeId.visible(true);
            tripStartDate.visible(true);
            tripEndDate.visible(true);
            periodTable.visible(true);
            tripGroup.visible(true);
            element.design().caption("@GLS106491");
            rHRMReportT9.enabled(true);
            rHRMReportT10.enabled(true);
            rHRMReportT10a.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::Vacation:
            vacationStartDate.visible(true);
            dayNumberTable.visible(true);
            vacationStartPeriod.visible(true);
            vacationEndPeriod.visible(true);
            vacationCodeId.visible(true);
            vacationGroup.visible(true);
            element.design().caption("@GLS106515");
            rhrmCreateVacationsFromSchedule.visible(element.args().record().TableId == tableNum(RHRMOrderTable));
            rhrmCreateVacationsFromSchedule.enabled(hrmOrderTable.Posted == NoYes::No);
            rHRMReportT6.enabled(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::StaffList:
            staffListOverview.visible(true);
            staffListGroup.visible(true);
            emplNameTable.visible(false);
            rhrmOrderTrans_EmplId.visible(false);
            element.design().caption("@RUP2437");
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(false);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(false);
            break;

        case RHRMJournalType::VacationChildCancel:
            prevVacationStartDate.visible(true);
            prevVacationEndDate.visible(true);
            tripStartDate.visible(true);
            tripStartDate.label("@RUP3197");
            tripStartDate.helpText("@RUP3197");
            vacationGroup.visible(true);
            element.design().caption("@RUP3196");
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationStartDate)).visible(true);
            rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, PrevVacationEndDate)).visible(true);
            vacationType.visible(false);
            vacationWorkType.visible(false);
            vacationTime.caption("@RUP3176");
            vacationTimeStartDate.label("@RUP3197");
            vacationTimeStartDate.helpText("@RUP3197");
            vacationTimeDayNumber.visible(false);
            vacationTimeEndDate.visible(false);
            vacationTimeDuration.visible(false);
            break;
    }
}

void enableFieldsActive()
{
    FormDataObject  staffListNoYes = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, StaffListNoYes));
    FormDataObject  organizationId = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, OrganizationId));
    FormDataObject  hrmPositionId  = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, HcmTitle));
    FormDataObject  minWage        = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, StaffListMinWage));
    FormDataObject  maxWage        = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, StaffListMaxWage));
    FormDataObject  empFactor      = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, EmploymentFactor));
    FormDataObject  wage           = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, ValueSum));
    FormDataObject  tradeCategory  = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, TradeCategory));

    staffListNoYes.allowEdit(! rhrmOrderTrans.TransferWithoutTermChanges);
    organizationId.allowEdit(! rhrmOrderTrans.TransferWithoutTermChanges);
    hrmPositionId.allowEdit(rhrmOrderTrans.StaffListNoYes == NoYes::No && ! rhrmOrderTrans.TransferWithoutTermChanges);
    tradeCategory.allowEdit(rhrmOrderTrans.StaffListNoYes == NoYes::No && ! rhrmOrderTrans.TransferWithoutTermChanges);
    empFactor.allowEdit(! rhrmOrderTrans.TransferWithoutTermChanges);

    rhrmReportT5.enabled(this.orderType() == RHRMJournalType::Transfer);

    if (this.orderType() == RHRMJournalType::StaffList)
    {
        minWage.allowEdit(! rhrmOrderTrans.GradeScaleId);
        maxWage.allowEdit(! rhrmOrderTrans.GradeScaleId);
    }

    if (this.orderType() == RHRMJournalType::Receive ||
        this.orderType() == RHRMJournalType::Transfer)
    {
        wage.allowEdit(! rhrmOrderTrans.GradeScaleId);
    }
}

void enableRates()
{
    employmentFactorRate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.EmploymentFactorRate, rhrmOrderTrans.EmplId));
    wageRate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.WageRate, rhrmOrderTrans.EmplId));
    riseInWages1Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages1Rate, rhrmOrderTrans.EmplId));
    riseInWages2Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages2Rate, rhrmOrderTrans.EmplId));
    riseInWages3Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages3Rate, rhrmOrderTrans.EmplId));

    emplFactorRateReceive.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.EmploymentFactorRate, rhrmOrderTrans.EmplId));
    wageRateReceive.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.WageRate, rhrmOrderTrans.EmplId));
    riseInWages1RateReceive.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages1Rate, rhrmOrderTrans.EmplId));
    riseInWages2RateReceive.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages2Rate, rhrmOrderTrans.EmplId));
    riseInWages3RateReceive.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages3Rate, rhrmOrderTrans.EmplId));
}

void enableWages()
{
    FormDataObject  fieldRiseInWages1;
    FormDataObject  fieldRiseInWages2;
    FormDataObject  fieldRiseInWages3;

    RHRMStaffList   rhrmStaffList;

    fieldRiseInWages1 = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, RiseInWages));
    fieldRiseInWages2 = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, RiseInWages2));
    fieldRiseInWages3 = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, RiseInWages3));

    if ( orderType != RHRMJournalType::StaffList )
    {
        employmentFactor.allowEdit(rhrmEmplTable.EmploymentFactorRate != "");
        transferEmploymentFactor.allowEdit(rhrmEmplTable.EmploymentFactorRate != "");
        if (rhrmEmplTable.EmploymentFactorRate == "")
            rhrmOrderTrans.EmploymentFactor = 0;

        valueSum.allowEdit(rhrmEmplTable.WageRate != "");
        transferValueSum.allowEdit(rhrmEmplTable.WageRate != "");
        if (rhrmEmplTable.WageRate == "")
            rhrmOrderTrans.ValueSum = 0;

        fieldRiseInWages1.allowEdit(rhrmEmplTable.RiseInWages1Rate != "");
        if (rhrmEmplTable.RiseInWages1Rate == "")
            rhrmOrderTrans.RiseInWages = 0;

        fieldRiseInWages2.allowEdit(rhrmEmplTable.RiseInWages2Rate != "");
        if (rhrmEmplTable.RiseInWages2Rate == "")
            rhrmOrderTrans.RiseInWages2 = 0;

        fieldRiseInWages3.allowEdit(rhrmEmplTable.RiseInWages3Rate != "");
        if (rhrmEmplTable.RiseInWages3Rate == "")
            rhrmOrderTrans.RiseInWages3 = 0;
    }
    else
    {
        rhrmStaffList = RHRMStaffList::find(rhrmOrderTrans.StaffListRefRecId);

        fieldRiseInWages1.allowEdit(rhrmStaffList && rhrmStaffList.RiseInWages1Rate != "");
        if (rhrmStaffList.RiseInWages1Rate == "")
            rhrmOrderTrans.RiseInWages = 0;

        fieldRiseInWages2.allowEdit(rhrmStaffList && rhrmStaffList.RiseInWages2Rate != "");
        if (rhrmStaffList.RiseInWages2Rate == "")
            rhrmOrderTrans.RiseInWages2 = 0;

        fieldRiseInWages3.allowEdit(rhrmStaffList && rhrmStaffList.RiseInWages3Rate != "");
        if (rhrmStaffList.RiseInWages3Rate == "")
            rhrmOrderTrans.RiseInWages3 = 0;
    }
}

RHRMOrderTable hrmOrderTable()
{
    return hrmOrderTable;
}

public void init()
{
    if (element.args().record().TableId == tableNum(RHRMOrderTable))
    {
        journalFormTrans = new RHRMJournalFormTrans(element);
    }

    super();

    if (element.args().record().TableId == tableNum(RHRMOrderTable))
    {
        hrmOrderTable = RHRMOrderTable::find(element.args().record().RecId);
        orderType     = element.args().caller().orderType();

        journalFormTrans.parmCtrlFunction(functions);
        journalFormTrans.parmCtrlCheckJournal(checkJournal);
        journalFormTrans.parmCtrlPostJournal(postJournal);
        journalFormTrans.parmCtrlUnPostJournal(unPostJournal);
        journalFormTrans.parmCtrlInfolog(showInfolog);
        journalFormTrans.parmCtrlLineLogImage(lineLogImage);

        journalFormTrans.formInitPost();
    }
    else
    {
        rhrmOrderTrans_ds.allowCreate(false);
        rhrmOrderTrans_ds.allowDelete(false);
        rhrmOrderTrans_ds.allowEdit(false);

        orderNum.visible(true);
        orderDate.visible(true);
        rhrmOrderTrans_EmplId.visible(false);
        emplNameTable.visible(false);

        checkJournal.visible(false);
        postJournal.visible(false);
        unPostJournal.visible(false);
        showInfolog.visible(false);
        functions.visible(false);

        orderType = element.args().parmEnum();

        queryBuildDataSource = rhrmOrderTrans_ds.query().dataSourceNo(1).addDataSource(tableNum(RHRMOrderTable));
    }

    this.enableFields();
}

protected void initButtons(Integer _journalTypeId)
{
}

public void initDatasourceAllow()
{
}

void joinToEmplTable()
{
    rhrmEmplTable.data(RHRMEmplTable::find(rhrmOrderTrans.EmplId));

    if (rhrmOrderTrans.EmplId && orderType == RHRMJournalType::Receive && !RHRMOrderTable::find(rhrmOrderTrans.OrderTableRecId).Posted)
    {
        rhrmEmplTable.initRates();
        rhrmOrderTrans.initRateValue(rhrmEmplTable);
    }

    this.enableRates();
    element.enableWages();
    rhrmEmplTable_DS.refresh();
}

JournalForm journalForm()
{
    return journalFormTrans;
}

RHRMJournalType orderType()
{
    return orderType;
}

void setStaffList(RHRMStaffList _staffList)
{
    RHRMStaffListHistory    rhrmStaffListHistory;
    RHRMParameters          rhrmParameters;

    rhrmOrderTrans.StaffListRefRecId    = _staffList.RecId;
    rhrmOrderTrans.HcmTitle             = _staffList.HcmTitle;
    rhrmOrderTrans.OrganizationId       = _staffList.OrganizationId;

    rhrmParameters = RHRMParameters::find();

    rhrmStaffListHistory = RHRMStaffListHistory::findLast(rhrmOrderTrans.StaffListRefRecId, rhrmOrderTrans.StartDate );

    if (orderType != RHRMJournalType::StaffList)
    {
        rhrmOrderTrans.GradeScaleId = rhrmStaffListHistory.GradeScaleId;
        rhrmOrderTrans.GradeId = rhrmStaffListHistory.MaxGradeId;

        rhrmOrderTrans.TradeCategory = _staffList.TradeCategoryId;

        if (! rhrmEmplTable.WageRate &&
            ! RPayRateTrans::existRateTrans(_staffList.WageRate, rhrmEmplTable.EmployeeId))
            rhrmEmplTable.WageRate             = _staffList.WageRate;

        if (! rhrmEmplTable.RiseInWages1Rate &&
            ! RPayRateTrans::existRateTrans(_staffList.RiseInWages1Rate, rhrmEmplTable.EmployeeId))
            rhrmEmplTable.RiseInWages1Rate     = _staffList.RiseInWages1Rate;

        if (! rhrmEmplTable.RiseInWages2Rate &&
            ! RPayRateTrans::existRateTrans(_staffList.RiseInWages2Rate, rhrmEmplTable.EmployeeId))
            rhrmEmplTable.RiseInWages2Rate     = _staffList.RiseInWages2Rate;

        if (! rhrmEmplTable.RiseInWages3Rate &&
            ! RPayRateTrans::existRateTrans(_staffList.RiseInWages3Rate, rhrmEmplTable.EmployeeId))
            rhrmEmplTable.RiseInWages3Rate     = _staffList.RiseInWages3Rate;

        if (! rhrmEmplTable.EmploymentFactorRate &&
            ! RPayRateTrans::existRateTrans(rhrmParameters.EmploymentFactorRate, rhrmEmplTable.EmployeeId))
        {
            rhrmEmplTable.EmploymentFactorRate = rhrmParameters.EmploymentFactorRate;
            rhrmOrderTrans.EmploymentFactor              = any2real(RPayRateTable::find(rhrmEmplTable.EmploymentFactorRate).RateDefaultValue);
        }

        if (rhrmEmplTable.WageRate)
            rhrmOrderTrans.ValueSum          = rhrmStaffListHistory.GradeScaleId ?
                                               rhrmStaffListHistory.maxGradeWage(rhrmOrderTrans.StartDate) :
                                               rhrmStaffListHistory.MaxWage;
        if (rhrmEmplTable.RiseInWages1Rate)
            rhrmOrderTrans.RiseInWages       = rhrmStaffListHistory.RiseInWages1;
        if (rhrmEmplTable.RiseInWages2Rate)
            rhrmOrderTrans.RiseInWages2      = rhrmStaffListHistory.RiseInWages2;
        if (rhrmEmplTable.RiseInWages3Rate)
            rhrmOrderTrans.RiseInWages3      = rhrmStaffListHistory.RiseInWages3;
    }
    else
    {
        rhrmOrderTrans.StaffListMaxWage      = rhrmStaffListHistory.GradeScaleId ?
                                               rhrmStaffListHistory.maxGradeWage(rhrmOrderTrans.StartDate) :
                                               rhrmStaffListHistory.MaxWage;
        rhrmOrderTrans.StaffListMinWage      = rhrmStaffListHistory.GradeScaleId ?
                                               rhrmStaffListHistory.minGradeWage(rhrmOrderTrans.StartDate) :
                                               rhrmStaffListHistory.MinWage;
        rhrmOrderTrans.GradeScaleId          = rhrmStaffListHistory.GradeScaleId;
        rhrmOrderTrans.StaffListMinGradeId   = rhrmStaffListHistory.MinGradeId;
        rhrmOrderTrans.StaffListMaxGradeId   = rhrmStaffListHistory.MaxGradeId;
        rhrmOrderTrans.ValueSum              = rhrmStaffListHistory.StaffListFactor;

        rhrmOrderTrans.RiseInWages           = _staffList.RiseInWages1Rate
                                                        ? rhrmStaffListHistory.RiseInWages1
                                                        : 0;
        rhrmOrderTrans.RiseInWages2          = _staffList.RiseInWages2Rate
                                                        ? rhrmStaffListHistory.RiseInWages2
                                                        : 0;
        rhrmOrderTrans.RiseInWages3          = _staffList.RiseInWages3Rate
                                                        ? rhrmStaffListHistory.RiseInWages3
                                                        : 0;

        rhrmOrderTrans.TradeCategory         = _staffList.TradeCategoryId;

        rhrmOrderTrans.Class                 = rHRMStaffListHistory.Class;
        rhrmOrderTrans.Certified             = rHRMStaffListHistory.Certified;
        rhrmOrderTrans.WorkCondClass         = rHRMStaffListHistory.WorkCondClass;
        rhrmOrderTrans.SpecCond              = rHRMStaffListHistory.SpecCond;
        rhrmOrderTrans.PositionList          = rHRMStaffListHistory.PositionList;
    }

    element.enableFieldsActive();

    rhrmOrderTrans_ds.refresh();
    element.enableWages();
}

void setStaffWageChangeTransfer()
{
    RPayHistory historyLast;

    if (rhrmOrderTrans.TransferWithoutTermChanges && rhrmOrderTrans.EmplId)
    {
        historyLast = RPayHistory::findLastSignOnMove(rhrmOrderTrans.EmplId);
        rhrmOrderTrans.StaffListRefRecId = historyLast.hrmStaffListRefRecId;
        rhrmOrderTrans.StaffListNoYes    = historyLast.staffList();
        rhrmOrderTrans.OrganizationId    = historyLast.HrmOrganizationId;
        rhrmOrderTrans.HcmTitle          = historyLast.HcmTitle;
        rhrmOrderTrans.TradeCategory     = historyLast.TradeCategory;
        rhrmOrderTrans.EmploymentFactor  = historyLast.employmentFactor();
    }
    else
    {
        rhrmOrderTrans.StaffListRefRecId = 0;
        rhrmOrderTrans.StaffListNoYes    = NoYes::No;
        rhrmOrderTrans.OrganizationId    = '';
        rhrmOrderTrans.HcmTitle          = 0;
        rhrmOrderTrans.TradeCategory     = '';
        rhrmOrderTrans.EmploymentFactor  = 0;
        this.enableWages();
    }
}

