public void executeQuery()
{
    if (element.args().record().TableId != tableNum(RHRMOrderTable))
    {
        queryBuildDataSource.relations(true);
        queryBuildDataSource.addRange(fieldNum(RHRMOrderTable, JournalType)).value(queryValue(orderType));
    }

    super();

    if (journalFormTrans)
    {
        journalFormTrans.datasourceExecuteQueryPost();
}
}

void create(boolean _append = false)
{
    if (journalFormTrans)
    {
        journalFormTrans.datasourceCreatePre();
    }

    super(_append);
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (journalFormTrans)
    {
        ret = journalFormTrans.datasourceValidateWritePost(ret);
    }

    return ret;
}

void write()
{
    if (journalFormTrans)
    {
        journalFormTrans.datasourceWritePre();
    }
    super();
    if (journalFormTrans)
    {
        journalFormTrans.datasourceWritePost();
    }

    this.clearDisplayOption(rhrmOrderTrans);
}

public int active()
{
    int     ret;

    ret = super();

    element.enableFieldsActive();

    if (journalFormTrans)
    {
        journalFormTrans.datasourceActivePost();
    }

    if (element.args().record().TableId != tableNum(RHRMOrderTable))
    {
        hrmOrderTable = RHRMOrderTable::find(rhrmOrderTrans.OrderTableRecId);
    }

    return ret;
}

void delete()
{
    if (journalFormTrans)
    {
        journalFormTrans.datasourceDeletePre();
    }
    super();

    if (journalFormTrans)
    {
        journalFormTrans.datasourceDeletePost();
        functions.enabled(journalFormTrans.journalTableData().journalTable().NumOfLines &&
                          ! journalFormTrans.journalTableData().journalTable().Posted);
    }
}

display container lineLogImage(RHRMOrderTrans _journalTrans)
{
    return journalFormTrans ?
           journalFormTrans.datasourceMethodLineLogImage(_journalTrans) :
           conNull();
}

void init()
{
    super();

    if (journalFormTrans)
    {
        journalFormTrans.datasourceInitPost(rhrmOrderTrans);
    }
}

public void initValue()
{
    if (journalFormTrans)
    {
        journalFormTrans.datasourceInitValuePre();
    }
    super();

    if (journalFormTrans)
    {
        journalFormTrans.datasourceInitValuePost();
    }
    rhrmOrderTrans.OrderTableRecId  = hrmOrderTable.RecId;
    rhrmOrderTrans.StartDate        = hrmOrderTable.TransDate;
    rhrmOrderTrans.DocArgCode       = hrmOrderTable.DocArgCode;
    rhrmOrderTrans.AttachDocDate    = hrmOrderTable.AttachDocDate;
    rhrmOrderTrans.AttachDocNum     = hrmOrderTable.AttachDocNum;

    if ( hrmOrderTable.JournalType == RHRMJournalType::StaffList )
    {
        rhrmOrderTrans.StaffListNoYes = NoYes::Yes;
    }

    if ( hrmOrderTable.JournalType == RHRMJournalType::Transfer )
    {
        rhrmOrderTrans.TransferWithoutTermChanges = hrmOrderTable.StaffWageChangeTransfer;
    }
}

