public void modified()
{
    super();

    if (hrmOrderTable.JournalType == RHRMJournalType::Vacation)
    {
        rhrmOrderTrans.setPeriodDate();
    }
}

public boolean validate()
{
    boolean     ret;

    ret = super();
    ret = ret && rhrmOrderTrans.setEndDate();
    return ret;
}

public void modified()
{
    RPayHistory    payHistory;
    TransDate      transDate;

    super();

    switch (orderType)
    {
        case RHRMJournalType::Transfer:
             element.joinToEmplTable();
             element.setStaffWageChangeTransfer();

        case RHRMJournalType::Receive,
             RHRMJournalType::Dismiss:
             payHistory = RPayHistory::find(rhrmOrderTrans.EmplId);
             transDate  = payHistory.EndDate ? payHistory.EndDate + 1 : payHistory.StartDate + 1;
             element.joinToEmplTable();
             break;

        case RHRMJournalType::Vacation,
             RHRMJournalType::Trip:
             transDate = RHRMEmplTable::find(rhrmOrderTrans.EmplId).PayEmploymentDate + 1;
             break;
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(RPayroll)))
    {
        transDate = max(transDate, dateStartMth(RPayParameters::payPeriod()));
    }

    if (rhrmOrderTrans.StartDate < transDate)
        rhrmOrderTrans.StartDate = transDate;

    rhrmOrderTrans_ds.refresh();
}

public boolean validate()
{
    boolean     ret;

    ret = super();
    if (element.orderType() == RHRMJournalType::Vacation)
    {
        rhrmOrderTrans.DayNumber = 0;
        ret = ret && rhrmOrderTrans.setDayNumber();
    }
    return ret;
}

public void modified()
{
    RHRMGradeScaleTable     rhrmGradeScaleTable;
    RHRMGradeScaleLine      rhrmGradeScaleLine;

    super();

    if (rhrmOrderTrans.GradeScaleId && rhrmOrderTrans.GradeId)
    {
        rhrmGradeScaleTable = RHRMGradeScaleTable::findActive(rhrmOrderTrans.GradeScaleId,
                                    rhrmOrderTrans.StartDate);

        rhrmGradeScaleLine = RHRMGradeScaleLine::find(rhrmGradeScaleTable.GradeScaleId, rhrmGradeScaleTable.StartDate, rhrmOrderTrans.GradeId);

        rhrmOrderTrans.ValueSum = rhrmGradeScaleLine.wage();
    }
    else
    {
        rhrmOrderTrans.ValueSum = 0;
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    RHRMGradeScaleLine::gradeLookup(rhrmOrderTrans.GradeScaleId, rhrmOrderTrans.StartDate, _formControl, _filterStr);
}

public void modified()
{
    FormDataObject datasourceField;

    super();

    rhrmOrderTrans.GradeId = 0;
    rhrmOrderTrans.StaffListMinGradeId = 0;
    rhrmOrderTrans.StaffListMaxGradeId = 0;

    if (element.orderType() == RHRMJournalType::Receive ||
        element.orderType() == RHRMJournalType::Transfer)
    {
        datasourceField = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, GradeId));
        datasourceField.modified();
    }

    if (element.orderType() == RHRMJournalType::StaffList)
    {
        datasourceField = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, StaffListMinGradeId));
        datasourceField.modified();

        datasourceField = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, StaffListMaxGradeId));
        datasourceField.modified();
    }

    element.enableFieldsActive();
}

public boolean validate()
{
    boolean       ret;
    RHRMStaffList hrmStaffListLocal;

    if (rhrmOrderTrans.StaffListNoYes)
    {
        hrmStaffListLocal = RHRMStaffList::find(any2int64(rhrmOrderTrans.OrganizationId));
        element.setStaffList(hrmStaffListLocal);
    }

    ret = super();

    return ret;
}

public boolean validate()
{
    boolean     ret;

    ret = super();
    ret = ret && rhrmOrderTrans.setEndDate();
    return ret;
}

public boolean validate()
{
    boolean                         ret;
    TransDate                       dateNull;
    RPayAverageEarnEmplPeriodTable  rPayAverageEarnEmplPeriodTable;

    ret = super();

    if (ret)
    {
        if (rhrmOrderTrans.PrevVacationStartDate)
        {
            select firstonly RecId, EndDate from rPayAverageEarnEmplPeriodTable
                where rPayAverageEarnEmplPeriodTable.StartDate          == rhrmOrderTrans.PrevVacationStartDate     &&
                      rPayAverageEarnEmplPeriodTable.EmplId             == rhrmOrderTrans.EmplId                    &&
                      rPayAverageEarnEmplPeriodTable.Included           == NoYes::Yes                               &&
                      rPayAverageEarnEmplPeriodTable.RpayIllType        == RPayIllType::ChildVacation;
            if (rPayAverageEarnEmplPeriodTable.RecId == 0)
            {
                ret = checkFailed(strFmt("@RUP3198", rhrmOrderTrans.PrevVacationStartDate, rhrmOrderTrans.EmplId));
            }
            else
            {
                rhrmOrderTrans.PrevVacationEndDate = rPayAverageEarnEmplPeriodTable.EndDate;
            }
        }
        else
        {
            rhrmOrderTrans.PrevVacationEndDate = dateNull;
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tableNum(RPayAverageEarnEmplPeriodTable),_formControl);
    Query                query = new Query();
    QueryBuildDataSource qBd;

    if (rhrmOrderTrans.EmplId)
    {
        qBd = query.addDataSource(tableNum(RPayAverageEarnEmplPeriodTable));

        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, StartDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, EndDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, RpaySickListId));

        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, AverageEarnCodeType)).value(queryValue(RPayAverageEarnCodeType::SickList));
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, EmplId)).value(rhrmOrderTrans.EmplId);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, Included)).value(queryValue(NoYes::Yes));
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, RpayIllType)).value(queryValue(RPayIllType::ChildVacation));

        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
}

