{
    RHRMGradeScaleTable rhrmGradeScaleTable;
    RHRMGradeScaleLine  rhrmGradeScaleLine;

    super();

    rhrmGradeScaleTable = RHRMGradeScaleTable::findActive(rhrmOrderTrans.GradeScaleId,
                                                          rhrmOrderTrans.StartDate );

    rhrmGradeScaleLine = RHRMGradeScaleLine::find(rhrmGradeScaleTable.GradeScaleId,
                                                  rhrmGradeScaleTable.StartDate,
                                                  rhrmOrderTrans.StaffListMaxGradeId);

    rhrmOrderTrans.StaffListMaxWage = rhrmGradeScaleLine.wage();
}

