    }
}

public boolean validateWrite()
{
    boolean ret;

    if (element.orderType() != RHRMJournalType::Receive ||
        element.orderType() != RHRMJournalType::Transfer)
    {
        ret = true;
    }
    else
    {
        ret = super();

        if (rhrmEmplTable.EmploymentFactorRate == '')
        {
            ret = ret && checkFailed("@GLS106589");
        }
    }

    return ret;
}

