Method.Source:
    [Form]
    public class RHRMVirtualNetworkHistoryLine extends FormRun
    {
        FormDataSource  parentDataSource;
}

public void init()
{
    super();

    parentDataSource = FormDataUtil::getFormDataSource(element.args().record());

    if (isConfigurationkeyEnabled(configurationKeyNum(RHRM)))
        rPayHistory_ds.allowDelete(false);
}

void setControls()
{
    signOn.visible(false);
    move.visible(false);
    dismiss.visible(false);

    switch (rPayHistory.OperationType)
    {
        case RHRMJournalType::Receive:
            signOn.visible(true);
            break;

        case RHRMJournalType::Transfer:
            move.visible(true);
            break;

        case RHRMJournalType::Dismiss:
            dismiss.visible(true);
            break;
    }
}

public void delete()
{
    super();

    parentDataSource.reread();
    parentDataSource.refresh();
}

public int active()
{
    int ret;

    ret = super();

    element.setControls();

    return ret;
}

void init()
{
    super();

    this.query().dataSourceNo(1).addSortField(fieldNum(RPayHistory, StartDate), SortOrder::Ascending);
    this.query().dataSourceNo(1).addSortField(fieldNum(RPayHistory, TransTime), SortOrder::Ascending);
}

void clicked()
{
    switch (rPayHistory.OperationType)
    {
        case RHRMJournalType::Receive:
            rhrmOrderTable.menuItemName(menuitemDisplayStr(RHRMOrderTableReceive));
            break;
        case RHRMJournalType::Transfer:
            rhrmOrderTable.menuItemName(menuitemDisplayStr(RHRMOrderTableTransfer));
            break;
        case RHRMJournalType::Dismiss:
            rhrmOrderTable.menuItemName(menuitemDisplayStr(RHRMOrderTableDismiss));
            break;
    }
    super();
}
