Method.Source:
    [Form]
    public class RHRMVirtualNetworkMove extends FormRun
    {
        boolean             okPressed;
        RPayHistory         lastHistory;
        RHRMEmployeeMove    rhrmEmployeeMove;

        RHRMJournalFormTable journalFormTable;
}

void enableFields()
{
    postionId.allowEdit(rhrmOrderTrans.StaffListNoYes == NoYes::No);
    hrmVirtualNetworkHistory_Category.allowEdit(rhrmOrderTrans.StaffListNoYes == NoYes::No);

    employmentFactor.allowEdit(rhrmEmplTable.EmploymentFactorRate != "");
    if (rhrmEmplTable.EmploymentFactorRate == "")
        rhrmOrderTrans.EmploymentFactor = 0;

    wage.allowEdit(rhrmEmplTable.WageRate != "" && !rhrmOrderTrans.GradeScaleId);
    if (rhrmEmplTable.WageRate == "")
        rhrmOrderTrans.ValueSum = 0;

    riseInWages.allowEdit(rhrmEmplTable.RiseInWages1Rate != "");
    if (rhrmEmplTable.RiseInWages1Rate == "")
        rhrmOrderTrans.RiseInWages = 0;

    riseInWages2.allowEdit(rhrmEmplTable.RiseInWages2Rate != "");
    if (rhrmEmplTable.RiseInWages2Rate == "")
        rhrmOrderTrans.RiseInWages2 = 0;

    riseInWages3.allowEdit(rhrmEmplTable.RiseInWages3Rate != "");
    if (rhrmEmplTable.RiseInWages3Rate == "")
        rhrmOrderTrans.RiseInWages3 = 0;
}

void enableRates()
{
    employmentFactorRate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.EmploymentFactorRate,
                                                                   rhrmEmplTable.EmployeeId));
    wageRate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.WageRate,
                                                       rhrmEmplTable.EmployeeId));
    riseInWages1Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages1Rate,
                                                               rhrmEmplTable.EmployeeId));
    riseInWages2Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages2Rate,
                                                               rhrmEmplTable.EmployeeId));
    riseInWages3Rate.allowEdit(! RPayRateTrans::existRateTrans(rhrmEmplTable.RiseInWages3Rate,
                                                               rhrmEmplTable.EmployeeId));
}

void init()
{
    journalFormTable = new RHRMJournalFormTable(element);

    super();
    lastHistory      = element.args().record();
    rhrmEmployeeMove = element.args().caller();
    element.setControls();
}

void run()
{
    super();

    try
    {
        rhrmOrderTable_ds.create();
        rhrmOrderTrans_ds.create();
        rhrmOrderTable_ds.allowCreate(false);
        rhrmOrderTrans_ds.allowCreate(false);
        element.enableFields();
    }
    catch (Exception::Error)
    {
        element.close();
    }
}

void setStaffList(RHRMStaffList _staffList)
{
    RHRMStaffListHistory    rhrmStaffListHistory;
    RHRMParameters          rhrmParameters;

    rhrmParameters = RHRMParameters::find();
    rhrmStaffListHistory = RHRMStaffListHistory::findLast(_staffList.RecId, rhrmOrderTrans.StartDate);

    rhrmOrderTrans.StaffListRefRecId    = _staffList.RecId;
    rhrmOrderTrans.HcmTitle             = _staffList.HcmTitle;
    rhrmOrderTrans.OrganizationId       = _staffList.OrganizationId;
    rhrmOrderTrans.TradeCategory        = _staffList.TradeCategoryId;
    rhrmOrderTrans.GradeScaleId         = rhrmStaffListHistory.GradeScaleId;
    rhrmOrderTrans.GradeId              = rhrmStaffListHistory.MaxGradeId;

    if (! rhrmEmplTable.WageRate &&
        ! RPayRateTrans::existRateTrans(_staffList.WageRate, rhrmEmplTable.EmployeeId))
        rhrmEmplTable.WageRate             = _staffList.WageRate;

    if (! rhrmEmplTable.RiseInWages1Rate &&
        ! RPayRateTrans::existRateTrans(_staffList.RiseInWages1Rate, rhrmEmplTable.EmployeeId))
        rhrmEmplTable.RiseInWages1Rate     = _staffList.RiseInWages1Rate;

    if (! rhrmEmplTable.RiseInWages2Rate &&
        ! RPayRateTrans::existRateTrans(_staffList.RiseInWages2Rate, rhrmEmplTable.EmployeeId))
        rhrmEmplTable.RiseInWages2Rate     = _staffList.RiseInWages2Rate;

    if (! rhrmEmplTable.RiseInWages3Rate &&
        ! RPayRateTrans::existRateTrans(_staffList.RiseInWages3Rate, rhrmEmplTable.EmployeeId))
        rhrmEmplTable.RiseInWages3Rate     = _staffList.RiseInWages3Rate;

    if (! rhrmEmplTable.EmploymentFactorRate &&
        ! RPayRateTrans::existRateTrans(rhrmParameters.EmploymentFactorRate, rhrmEmplTable.EmployeeId))
    {
        rhrmEmplTable.EmploymentFactorRate = rhrmParameters.EmploymentFactorRate;
        rhrmOrderTrans.EmploymentFactor              = any2real(RPayRateTable::find(rhrmEmplTable.EmploymentFactorRate).RateDefaultValue);
    }

    if (rhrmEmplTable.WageRate)
        rhrmOrderTrans.ValueSum          = rhrmStaffListHistory.MaxWage;
    if (rhrmEmplTable.RiseInWages1Rate)
        rhrmOrderTrans.RiseInWages       = rhrmStaffListHistory.RiseInWages1;
    if (rhrmEmplTable.RiseInWages2Rate)
        rhrmOrderTrans.RiseInWages2      = rhrmStaffListHistory.RiseInWages2;
    if (rhrmEmplTable.RiseInWages3Rate)
        rhrmOrderTrans.RiseInWages3      = rhrmStaffListHistory.RiseInWages3;

    rhrmOrderTrans_ds.refresh();
    element.enableFields();
}

public void initValue()
{
    TransDate transDate;

    transDate = lastHistory.EndDate ? lastHistory.EndDate + 1 : lastHistory.StartDate + 1;
    transDate = rhrmOrderTable.TransDate > transDate ? rhrmOrderTable.TransDate : transDate;

    if (isConfigurationkeyEnabled(configurationKeyNum(RPayroll)))
    {
        transDate = max(transDate, dateStartMth(RPayParameters::payPeriod()));
    }

    rhrmOrderTrans.StartDate = transDate;

    rhrmOrderTrans.EmplId            = rhrmEmployeeMove.emplId();

    rhrmOrderTrans.OrderTableRecId   = rhrmOrderTable.RecId;

    rhrmOrderTrans.KindOfWork        = RHRMEmplTable::find(rhrmOrderTrans.EmplId).KindOfWork;

    rhrmOrderTrans.EmploymentFactor  = lastHistory.employmentFactor();
    rhrmOrderTrans.ValueSum          = lastHistory.wage();
    rhrmOrderTrans.RiseInWages       = lastHistory.riseInWages();
    rhrmOrderTrans.RiseInWages2      = lastHistory.riseInWages2();
    rhrmOrderTrans.RiseInWages3      = lastHistory.riseInWages3();
}

public void modified()
{
    RHRMGradeScaleTable     rhrmGradeScaleTable;
    RHRMGradeScaleLine      rhrmGradeScaleLine;

    super();

    if (rhrmOrderTrans.GradeScaleId && rhrmOrderTrans.GradeId)
    {
        rhrmGradeScaleTable = RHRMGradeScaleTable::findActive(rhrmOrderTrans.GradeScaleId, rhrmOrderTrans.StartDate);

        rhrmGradeScaleLine = RHRMGradeScaleLine::find(rhrmGradeScaleTable.GradeScaleId, rhrmGradeScaleTable.StartDate, rhrmOrderTrans.GradeId);

        rhrmOrderTrans.ValueSum = rhrmGradeScaleLine.wage();
    }
    else
    {
        rhrmOrderTrans.ValueSum = 0;
    }
}

public void modified()
{
    FormDataObject datasourceField;

    super();

    rhrmOrderTrans.GradeId = 0;

    datasourceField = rhrmOrderTrans_ds.object(fieldNum(RHRMOrderTrans, GradeId));
    datasourceField.modified();

    element.enableFields();
}

public boolean validate()
{
    boolean       ret;
    RHRMStaffList hrmStaffListLocal;

    if (rhrmOrderTrans.StaffListNoYes)
    {
        hrmStaffListLocal = RHRMStaffList::find(str2int64(rhrmOrderTrans.OrganizationId));
        element.setStaffList(hrmStaffListLocal);
    }

    ret = super();

    return ret;
}

public void modified()
{
    RHRMStaffList rhrmStaffListLocal;

    super();
    element.setStaffList(rhrmStaffListLocal);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    return ret;
}

void clicked()
{
    RHRMJournalCheckPost  journalCheckPost;

    okPressed = true;

    try
    {
        ttsbegin;

        if (rhrmEmplTable.EmploymentFactorRate == "")
            throw error("@GLS106589");

        if (! rhrmOrderTable_ds.validateWrite())
            throw error("@GLS101992");

        if (! rhrmEmplTable_ds.validateWrite())
            throw error("@GLS101992");

        rhrmOrderTable.insert();
        rhrmOrderTrans.OrderTableRecId = rhrmOrderTable.RecId;
        rhrmOrderTrans.JournalId       = rhrmOrderTable.JournalId;

        if (! rhrmOrderTrans_ds.validateWrite())
            throw error("@GLS101992");

        rhrmOrderTrans.insert();

        rhrmEmplTable.update();

        journalCheckPost = new RHRMJournalCheckPost_Transfer();
        journalCheckPost.parmJournalCheckPostType(JournalCheckPostType::Post);
        journalCheckPost.parmJournalTableId(rhrmOrderTable.TableId);
        journalCheckPost.parmJournalId(rhrmOrderTable.JournalId);

        journalCheckPost.runOperation();

        rhrmOrderTable_ds.reread();

        if (!rhrmOrderTable.Posted)
        {
            throw Exception::Error;
        }

        ttscommit;

        super();
    }
    catch (Exception::Error)
    {
        okPressed = false;
    }
}
