public void refreshEmployee(RHRMEmplId  _emplId)
{
    if (_emplId)
    {
        emplTable_DS.executeQuery();
        emplTable_DS.positionToRecordByValue(fieldNum(RHRMEmplTable, EmployeeId), queryValue(_emplId));
        emplTable_DS.active();
    }
    else
    {
        emplTable_DS.research();
    }
}

public void setEmploymentButtons()
{
    if (emplTable.employmentStatus() == RHRMEmploymentStatus::Employed)
    {
        rhrmEmployeeEmploy.enabled(false);
        rhrmEmployeeMove.enabled(true);
        rhrmEmployeeDismiss.enabled(true);
    }
    else
    {
        rhrmEmployeeEmploy.enabled(true);
        rhrmEmployeeMove.enabled(false);
        rhrmEmployeeDismiss.enabled(false);
    }
}

public int active()
{
    int ret;

    ret = super();
    element.setEmploymentButtons();

    return ret;
}
