Method.Source:
    [Form]
    public class RPayAverageEarnEmplPeriodTable extends FormRun
    {
        QueryBuildRange       criteriaEarnPayPeriod;
        RPayAverageEarnEngine rPayAverageEarnEngine;

        boolean viewMode;
}

public void init()
{
    Object  o;

    if (element.args() && element.args().caller())
    {
        o = element.args().caller();
        viewMode = o is RTax25OriginalDocuments;
    }

    super();

    payPeriodEdit.dateValue(RPayParameters::payPeriod());

    rPayAverageEarnEngine = new RPayAverageEarnEngine(rPayAverageEarnEmplPeriodTable.EmplId,
                                                      rPayAverageEarnEmplPeriodTable.rPayAverageEarnCode,
                                                      endmth(payPeriodEdit.dateValue()));

    rPayAverageEarnEngine.formRun(this);
    rPayAverageEarnEngine.viewMode(viewMode);
    payPeriodEdit.visible(!rPayAverageEarnEngine.viewMode());
    functions.enabled(!rPayAverageEarnEngine.viewMode());
    buttonAddString.enabled(!rPayAverageEarnEngine.viewMode());
    buttonDeleteString.enabled(!rPayAverageEarnEngine.viewMode());
}

public void write()
{
    super();
    rPayAverageEarnEngine.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
    rPayAverageEarnEngine.calculateNow();
    this.refresh();
    this.reread();

    rPayAverageEarnTrans_ds.research();
}

public void create(boolean _append = false)
{
    super(_append);
    rPayAverageEarnEmplPeriodTable.AverageEarnCalcPeriod = endmth(payPeriodEdit.dateValue());
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
        rPayAverageEarnEngine.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
    }

    return ret;
}

public void executeQuery()
{
    if (viewMode)
    {
        criteriaEarnPayPeriod.value(SysQuery::valueUnlimited());
    }
    else
    {
        criteriaEarnPayPeriod.value(queryValue(endmth(payPeriodEdit.dateValue())));
    }
    super();
}

public void init()
{
    QueryBuildDataSource rPayAverageEarnEmplPeriodTableDs,
                         rPayAverageEarnTableDs;

    super();
    this.cacheAddMethod(tableMethodStr(RPayAverageEarnEmplPeriodTable,payEmplName));
    this.cacheAddMethod(tableMethodStr(RPayAverageEarnEmplTable,averageEarnName));
    rPayAverageEarnEmplPeriodTableDs = rPayAverageEarnEmplPeriodTable_Q.dataSourceNo(1);
    criteriaEarnPayPeriod =  rPayAverageEarnEmplPeriodTableDs.addRange(fieldNum(RPayAverageEarnEmplPeriodTable,AverageEarnCalcPeriod));
    rPayAverageEarnTableDs     = rPayAverageEarnEmplPeriodTableDs.addDataSource(tableNum(RPayAverageEarnTable));
    if (!viewMode)
        rPayAverageEarnTableDs.addRange(fieldNum(RPayAverageEarnTable,RpayAverageEarnCodeType)).value(queryValue(RPayAverageEarnCodeType::Other));
    rPayAverageEarnTableDs.joinMode(JoinMode::ExistsJoin);
    rPayAverageEarnTableDs.relations(true);
}

void clicked()
{
    rPayAverageEarnEngine.createLines(true);
    if (rPayAverageEarnEngine.prompt())
        rPayAverageEarnEngine.runOperation();

    rPayAverageEarnTrans_ds.research();
    rPayAverageEarnEmplPeriodTable_ds.research();
}

void clicked()
{
    rPayAverageEarnEngine.createLines(false);

    if (rPayAverageEarnEngine.prompt())
        rPayAverageEarnEngine.runOperation();

    rPayAverageEarnTrans_ds.research();
    rPayAverageEarnEmplPeriodTable_ds.research();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        rPayAverageEarnEmplPeriodTable_ds.executeQuery();
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (! (select firstonly rpayAverageEarnTable where rpayAverageEarnTable.RpayAverageEarnCodeType == RPayAverageEarnCodeType::Other &&
                                                           rpayAverageEarnTable.rPayAverageEarnCode     == this.text()).RecId)
        {
            error("@GLS106110");
            ret = false;
        }
    }
    return ret;
}
