[Form]
public class RPayBusinessTripTable extends FormRun
{
    #define.MainTabSheetNum(1)
    #define.GeneralTabSheetNum(2)
    RPayAverageEarnEngine_BusinessTrip  rPayAverageEarnEngine_BusinessTrip;
}

display  RPayAmount averageOtherFund()
{
    return  rPayAverageEarnEngine_BusinessTrip.calcAverageAmount(RPayAverageSourceType::OtherFund);
}

display  RPayAmount averagePaymentFund()
{
    return rPayAverageEarnEngine_BusinessTrip.calcAverageAmount(RPayAverageSourceType::PaymentFund);
}

boolean checkSpecTime()
{
    boolean     ret;

    rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
    ret = rPayAverageEarnEngine_BusinessTrip.check();

    if (ret)
    {
        if (rPayAverageEarnEmplPeriodTable.BusinessTripRecordType == RPayBusinessTripRecordType::BusinessTripBreaking)
        {
            if (ret)
            {
                switch (element.formController().needTSUpdate())
                {
                    case UnknownNoYes::Unknown:
                        if (Box::yesNo(strFmt(strFmt("@SYS76498", "@RUP1253",  "@RUP6011" + '?'), rPayAverageEarnEmplPeriodTable.EmplId, RHRMEmplTable::find(rPayAverageEarnEmplPeriodTable.EmplId).name()), DialogButton::Yes,"@RUP6011", "@RUP971") == DialogButton::Yes)
                        {
                            ret = true;
                        }
                        else
                        {
                            ret = false;
                        }
                    case UnknownNoYes::No:
                        ret = false;
                        break;
                    case UnknownNoYes::Yes:
                        ret = true;
                        break;
                }
            }
        }
    }

    return ret;
}

public RPayAEFormController formController()
{
    return rPayAverageEarnEngine_BusinessTrip.formController();
}

public void init()
{
    super();
    rPayAverageEarnEngine_BusinessTrip = new RPayAverageEarnEngine_BusinessTrip(rPayAverageEarnEmplPeriodTable.EmplId,
                                                                        rPayAverageEarnEmplPeriodTable.rPayAverageEarnCode,
                                                                        rPayAverageEarnEmplPeriodTable.AverageEarnCalcPeriod);
    rPayAverageEarnEngine_BusinessTrip.formRun(this);
}

void modifyBusinessTripTypeId()
{
    if (rpayAverageEarnEmplPeriodTable.BusinessTripTypeId)
    {
        rpayAverageEarnEmplPeriodTable.rPayAverageEarnCode = RHRMBusinessTripType::find(rpayAverageEarnEmplPeriodTable.BusinessTripTypeId).AverageEarnCode;
        if (rpayAverageEarnEmplPeriodTable.rPayAverageEarnCode)
        {
            rPayAverageEarnEmplPeriodTable.initFromAverageEarnEmplTable();
            rPayAverageEarnEmplPeriodTable_ds.cacheCalculateMethod(tableMethodStr(RPayAverageEarnEmplPeriodTable,averageEarnName));
            element.updateDesign();
        }

        element.updateDayNumber();
    }
}

boolean updateDayNumber()
{
    boolean     ret = true;
    ;
    if (rPayAverageEarnEmplPeriodTable.StartDate && rPayAverageEarnEmplPeriodTable.EndDate)
    {
        rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        ret = rPayAverageEarnEngine_BusinessTrip.updateDayNumber();
    }

    return ret;
}

void updateDesign()
{
    ;
    rPayAverageEarnEngine_BusinessTrip.updateFormDesign();
}

public void updateTimesheet(RPayAverageEarnEmplPeriodTable _document, StartDate _startDate, EndDate _endDate)
{
    if (rPayAverageEarnEngine_BusinessTrip.formController().needTSUpdate() == UnknownNoYes::Yes)
    {
        RPayTblUpdater::updateTimesheetFromDocument(_startDate, _endDate, _document.EmplId, [_document.TableId, _document.RecId]);
    }
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(RPayAverageEarnEmplPeriodTable, payEmplName));
}

public void delete()
{
    RPayAverageEarnEmplPeriodTable rPayAverageEarnEmplPeriodTableOrig;
    boolean                        isReverseLine = rPayAverageEarnEmplPeriodTable.isReverseLine();

    rPayAverageEarnEmplPeriodTableOrig = rPayAverageEarnEmplPeriodTable.data();

    super();

    if (element.formController().needTSUpdate() && rPayAverageEarnEmplPeriodTableOrig.RecId)
    {
        rPayAverageEarnEmplPeriodTableOrig.RecId = 0;
        element.updateTimesheet(rPayAverageEarnEmplPeriodTableOrig,
                                rPayAverageEarnEmplPeriodTableOrig.StartDate,
                                rPayAverageEarnEmplPeriodTableOrig.EndDate);
    }
}

public boolean validateWrite()
{
    boolean              ret = true;

    if ( !rPayAverageEarnEmplPeriodTable.Included)
    {
        rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        ret = rPayAverageEarnEngine_BusinessTrip.check();

        if (ret && rPayAverageEarnEmplPeriodTable.BusinessTripRecordType == RPayBusinessTripRecordType::BusinessTrip)
        {
            ret = RpayTblSpecTimeTrans::checkEndDate(RpayTblSpecTimeType::Mission,
                                                     rPayAverageEarnEmplPeriodTable.EmplId,
                                                     rPayAverageEarnEmplPeriodTable.StartDate,
                                                     rPayAverageEarnEmplPeriodTable.EndDate);
        }
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public void executeQuery()
{
    this.query().dataSourceTable(tableNum(RPayAverageEarnEmplPeriodTable)).
        addRange(fieldNum(RPayAverageEarnEmplPeriodTable, AverageEarnCodeType)).value(queryValue(RPayAverageEarnCodeType::BusinessTrip));

    if (element.args().dataset() == tableNum(RPayAverageEarnEmplPeriodTable))
    {
        this.query().dataSourceTable(tableNum(RPayAverageEarnEmplPeriodTable)).
         addRange(fieldNum(RPayAverageEarnEmplPeriodTable, RecId)).value(con2Str(TransLink_RU::rpayGetReverseInCon(element.args().record())));
    }

    super();
}

public void write()
{
    RPayAverageEarnEmplPeriodTable origTable;

    origTable = rPayAverageEarnEmplPeriodTable.orig();
    super();

    if ( !rPayAverageEarnEmplPeriodTable.Included)
    {
        if (element.formController().needTSUpdate()
          && (origTable.StartDate          != rPayAverageEarnEmplPeriodTable.StartDate ||
            origTable.EndDate            != rPayAverageEarnEmplPeriodTable.EndDate   ||
            origTable.BusinessTripTypeId != rPayAverageEarnEmplPeriodTable.BusinessTripTypeId))
        {
            if (origTable.RecId)
            {
                element.updateTimesheet(origTable,
                                        min(origTable.StartDate, rPayAverageEarnEmplPeriodTable.StartDate),
                                        max(origTable.EndDate, rPayAverageEarnEmplPeriodTable.EndDate));
            }
            else
            {
                element.updateTimesheet(rPayAverageEarnEmplPeriodTable, rPayAverageEarnEmplPeriodTable.StartDate, rPayAverageEarnEmplPeriodTable.EndDate);
            }
        }
        if ( !bufCmp(rPayAverageEarnEmplPeriodTable, origTable))
        {
            rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
            rPayAverageEarnEngine_BusinessTrip.calculateNow();
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        element.updateDesign();
    }

    return ret;
}

