    }
}

public boolean validate()
{
    boolean ret;
    ;
    ret = super();
    if (ret && rPayAverageEarnEmplPeriodTable.PrevBusinessTripStartDate)
    {
        if (! rPayAverageEarnEmplPeriodTable.previousBusinessTrip())
        {
            ret = checkFailed(strFmt("@RUP2628", rPayAverageEarnEmplPeriodTable.PrevBusinessTripStartDate, rPayAverageEarnEmplPeriodTable.EmplId));
        }
        else
        {
            rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
            rPayAverageEarnEngine_BusinessTrip.initFromPreviousBusinessTrip();
        }
    }

    if (ret)
    {
        element.updateDesign();
    }
    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tableNum(RPayAverageEarnEmplPeriodTable),_formControl);
    Query                query = new Query();
    QueryBuildDataSource qBd;
    ;
    if (rPayAverageEarnEmplPeriodTable.EmplId && rPayAverageEarnEmplPeriodTable.rPayAverageEarnCode)
    {
        qBd = query.addDataSource(tableNum(RPayAverageEarnEmplPeriodTable));

        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, StartDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, EndDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, BusinessTripTypeId));

        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, rPayAverageEarnCode)).value(rPayAverageEarnEmplPeriodTable.rPayAverageEarnCode);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, EmplId)).value(rPayAverageEarnEmplPeriodTable.EmplId);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, BusinessTripTypeId)).value(rPayAverageEarnEmplPeriodTable.BusinessTripTypeId);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, Included)).value(queryValue(NoYes::Yes));
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, BusinessTripRecordType)).value(queryValue(RPayBusinessTripRecordType::BusinessTrip));

        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
}

public void lookup(FormControl _formControl, str _filterStr)
