#define.MainTabSheetNum(1)
        #define.GeneralTabSheetNum(2)
public boolean tabChange(int _fromTab)
        {
            boolean ret = true;

            if (_fromTab == #MainTabSheetNum || _fromTab == #GeneralTabSheetNum)
            {
                //enforce writing to prevent active() calling
                ret = rpayAverageEarnEmplPeriodTable_ds.validateWrite() && rPayAverageEarnEngine_BusinessTrip.check();
                if (ret)
                {
                    rpayAverageEarnEmplPeriodTable_ds.write();
                }
            }

            return super(_fromTab);
        }

void clicked()
{
    ;
    if (! rPayAverageEarnEmplPeriodTable.Included)
    {
        rPayAverageEarnEngine_BusinessTrip.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        rPayAverageEarnEngine_BusinessTrip.calculateNow();
        rPayAverageEarnEmplPeriodTable_ds.refresh();
        rPayAverageEarnEmplPeriodTable_ds.reread();
        rPayAverageEarnTrans_ds.research();
    }
    else
    {
        info ("@RUP2629");
    }
}
