    rpaySheetTrans_ds.research();
}

void refreshRPaySheetTable()
{
    rPaySheetTable_ds.reread();
    rPaySheetTable_ds.refresh();
}

void run()
{
    super();
    if (!rPaySheetTable.RecId && showAll.selection() != RPayStatusRButton::All)
    {
        showAll.selection(RPayStatusRButton::All);
        rPaySheetTable_ds.executeQuery();
    }
}

void setAccess()
{
    RPayDepositTable    payDepositTable;
    RPaySheetTrans      rPaySheetTransLocal;
    LedgerJournalTable  ledgerJournalTableLocal;

    if (rPaySheetTable.PaySheetSost == RPaySheetSost::Paid ||
        rPaySheetTable.CashOrderNum)
    {
        closed.enabled(false);
        rPaySheetTable_ds.allowEdit(false);
        rPaySheetTransBank_ds.allowDelete(rPaySheetTable.PaySheetSost != RPaySheetSost::Paid);
        rPayDepositTable_ds.allowDelete(rPaySheetTable.PaySheetSost != RPaySheetSost::Paid);
    }
    else
    {
        select firstonly RecId from rPaySheetTransLocal
            where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
                  rPaySheetTransLocal.SendToBank      == NoYes::No            &&
                  rPaySheetTransLocal.SendToDeposit   == NoYes::No;

        closed.enabled(rPaySheetTransLocal.RecId);
        rPaySheetTable_ds.allowEdit(true);
        rPaySheetTransBank_ds.allowDelete(true);
        rPayDepositTable_ds.allowDelete(true);
    }
    if (rPaySheetTransBank.JournalNum)
    {
        ledgerJournalTableLocal = LedgerJournalTable::find(rPaySheetTransBank.JournalNum);

        createBankPayment.enabled(false);
        deleteBankPayment.enabled(! ledgerJournalTableLocal.Posted);
        postBankJournal.enabled(! ledgerJournalTableLocal.Posted);
        bankJournal.enabled(true);
    }
    else
    {
        createBankPayment.enabled(true);
        deleteBankPayment.enabled(false);
        postBankJournal.enabled(false);
        bankJournal.enabled(false);
    }

    select firstonly RecId from payDepositTable
        where payDepositTable.RefPaySheetTableRecId == rPaySheetTable.RecId &&
              ! payDepositTable.Voucher;

    createDeposit.enabled(payDepositTable.RecId);

    select firstonly RecId from rPaySheetTransLocal
        where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
              rPaySheetTransLocal.SendToBank      == NoYes::Yes           &&
              ! rPaySheetTransLocal.JournalNum;

    createBankPayment.enabled(rPaySheetTransLocal.RecId);

    select firstonly RecId from rPaySheetTransLocal
        where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
              rPaySheetTransLocal.BankPayment     == NoYes::Yes           &&
              rPaySheetTransLocal.SendToBank      == NoYes::No;

    send2Bank.enabled(rPaySheetTransLocal.RecId       &&
                      (! rPaySheetTable.CashOrderNum) &&
                      rPaySheetTable.PaySheetSost != RPaySheetSost::Paid);

    select firstonly RecId from rPaySheetTransLocal
        where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
              rPaySheetTransLocal.Deposit         == NoYes::Yes           &&
              rPaySheetTransLocal.SendToDeposit   == NoYes::No;

    send2Deposit.enabled(rPaySheetTransLocal.RecId       &&
                         (! rPaySheetTable.CashOrderNum) &&
                         rPaySheetTable.PaySheetSost != RPaySheetSost::Paid);
}

int active()
{
    int ret;

    ret = super();

    element.setAccess();
    voucher.enabled(false);

    return ret;
}

void executeQuery()
{
    if (showAll.selection() == RPayStatusRButton::All)
    {
        showSheetTable.value(SysQuery::valueNot(enum2str(RPaySheetSost::NoPaid)));
    }
    else if (showAll.selection() ==  RPayStatusRButton::Paid)
    {
        showSheetTable.value(enum2str(RPaySheetSost::Paid));
    }
    else
    {
        showSheetTable.value(enum2str(RPaySheetSost::ForPaid));
    }

    super();
}

void init()
{
    super();

    showSheetTable = this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTable, PaySheetSost));
    showSheetTable.status(RangeStatus::Hidden);
}

display ResourceId checkIfLines(RPaySheetTable _rPaySheetTable)
{
    if (_rPaySheetTable.PaySheetSost == RPaySheetSost::Paid)
    {
        return #ImageOk;
    }
    else if (_rPaySheetTable.PaySheetDateTo - DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) > 2)
    {
        return #ImageInfo;
    }
    else if (_rPaySheetTable.PaySheetDateTo - DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) >= 0)
    {
        return #ImageWarning;
    }
    else
    {
        return #ImageError;
    }
}

void init()
{
    QueryBuildRange     queryBuildRange;

    super();

    queryBuildRange = this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTrans, SendToBank));
    queryBuildRange.value(enum2str(NoYes::No));
