    queryBuildRange = this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTrans, SendToDeposit));
    queryBuildRange.value(enum2str(NoYes::No));
    queryBuildRange.status(RangeStatus::Hidden);
}

