void delete()
{
    RPaySheetTrans  rPaySheetTransLocal, rPaySheetTransUpdate;

    if (! rPaySheetTable.checkUpdate())
    {
        return;
    }

    ttsbegin;

    for (rPaySheetTransLocal = rPaySheetTransBank_ds.getFirst(true) ?
                               rPaySheetTransBank_ds.getFirst(true) :
                               rPaySheetTransBank_ds.cursor();
        rPaySheetTransLocal;
        rPaySheetTransLocal = rPaySheetTransBank_ds.getNext())
    {
        rPaySheetTransUpdate = RPaySheetTrans::find(rPaySheetTransLocal.Emplid, rPaySheetTransLocal.SheetTableRecId, true);
        if (rPaySheetTransUpdate.JournalNum)
        {
            continue;
        }

        rPaySheetTransUpdate.SendToBank = NoYes::No;
        rPaySheetTransUpdate.update();
        if (! rPaySheetTransUpdate.checkLastRecordBankPayment())
        {
            rPaySheetTable.BankPayment = NoYes::No;
            rPaySheetTable.update();
        }
        if (rPaySheetTable.CashOrderNum)
        {
            rPaySheetTransUpdate.BankPayment       = NoYes::No;
            rPaySheetTransUpdate.Deposit           = NoYes::Yes;
            rPaySheetTransUpdate.SendToDeposit     = NoYes::Yes;
            rPaySheetTransUpdate.EmplBankAccountId = "";
            rPaySheetTransUpdate.update();
        }
    }

    ttscommit;

    if (rPaySheetTable.CashOrderNum)
    {
        rPaySheetTable.createPayDeposit();
    }

    rPayDepositTable_ds.research();
    element.setAccess();

    rpaySheetTransBank_ds.executeQuery();
    rpaySheetTrans_ds.executeQuery();
}

void init()
{
    QueryBuildRange    queryBuildRange;

    super();
    this.cacheAddMethod(tablemethodstr(RPaySheetTrans,getShortEmplName));
    this.cacheAddMethod(tablemethodstr(RPaySheetTrans,postedBank));

    queryBuildRange = this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTrans,SendToBank));
    queryBuildRange.value(enum2str(NoYes::Yes));
    queryBuildRange.status(RangeStatus::Hidden);
}

