void clicked()
{
    RPaySheetTrans     rPaySheetTransLocal;

    rCashTrans.enabled(rPaySheetTable.CashOrderNum ? true : false);

    select firstOnly RecId from rPaySheetTransLocal
        where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
              rPaySheetTransLocal.SendToDeposit   == NoYes::Yes;

    depositTable.enabled(rPaySheetTransLocal.RecId);

    select firstOnly RecId from rPaySheetTransLocal
        where rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId &&
              rPaySheetTransLocal.SendToBank      == NoYes::Yes;

    bankPayment.enabled(rPaySheetTransLocal.RecId);

    super();
}

void clicked()
{
    RPaySheetTrans  rPaySheetTransLocal;

    if (! rPaySheetTable.checkUpdate())
    {
        return;
    }

    ttsbegin;

    while select forupdate rPaySheetTransLocal
        where rPaySheetTransLocal.BankPayment     == NoYes::Yes           &&
              rPaySheetTransLocal.SheetTableRecId == rPaySheetTable.RecId
    {
        rPaySheetTable.BankPayment = NoYes::Yes;
        rPaySheetTable.update();
        rPaySheetTransLocal.SendToBank        =  NoYes::Yes;
        rPaySheetTransLocal.EmplBankAccountId =  RHRMPersonBankAccount::findEmplId(rPaySheetTransLocal.Emplid).AccountID;
        rPaySheetTransLocal.update();
    }

    ttscommit;

    element.refreshForm();
    rPaySheetTable_ds.refresh();
    rPaySheetTable_ds.reread();
}

