void clicked()
{
    LedgerJournalTable  ledgerJournalTableLocal;
    LedgerJournalTrans  ledgerJournalTrans;
    int                 countTrans, i;
    RPaySheetTrans      rPaySheetTransQ;
    container           con, conItem;
    RPaySheetTable      rPaySheetTableLocal;
    RPayAmount          amount;

    select firstOnly RecId from rPaySheetTransQ
        where rPaySheetTransQ.JournalNum == rPaySheetTransBank.JournalNum;

    countTrans = any2Int(rPaySheetTransQ.RecId);

    while select sum(Amount) from rPaySheetTransQ group by SheetTableRecId
        where rPaySheetTransQ.JournalNum == rPaySheetTransBank.JournalNum
    {
        conItem =  [rPaySheetTransQ.SheetTableRecId, rPaySheetTransQ.Amount];
        con     += [conItem];
    }

    ttsbegin;

    ledgerJournalTableLocal = LedgerJournalTable::find(rPaySheetTransBank.JournalNum, true);

    while select forupdate ledgerJournalTrans
        where ledgerJournalTrans.JournalNum == rPaySheetTransBank.JournalNum
    {
        if (ledgerJournalTrans.validateDelete())
        {
            ledgerJournalTrans.delete();
        }
    }

    if (ledgerJournalTableLocal.validateDelete())
    {
        ledgerJournalTableLocal.delete();

        for (i = 1; i <= conlen(con); i ++)
        {
            conItem             = conpeek(con, i);
            rPaySheetTableLocal = RPaySheetTable::find(conpeek(conItem, 1), true);
            amount              = conpeek(conItem, 2);
            rPaySheetTableLocal.updateDisbursedSum(- amount);
        }

        ttscommit;
        info("@SYS13435");
    }
    else
    {
        ttsabort;
    }

    if (countTrans > 1)
    {
        rPaySheetTransBank_ds.executeQuery();
    }
    else
    {
        rPaySheetTransBank_ds.reread();
        rPaySheetTransBank_ds.refresh();
        element.setAccess();
    }

    element.refreshRPaySheetTable();
}

void clicked()
{
    LedgerJournalCheckPost ledgerJournalCheckPost;

    ledgerJournalCheckPost = LedgerJournalCheckPost::newLedgerJournalTable(LedgerJournalTable::find(rPaySheetTransBank.JournalNum,true),NoYes::Yes);
    LedgerJournalCheckPost::processOperation(ledgerJournalCheckPost);

    rPaySheetTransBank_ds.cacheCalculateMethod(tablemethodstr(RPaySheetTrans,postedBank));
}

